import React from 'react';
import { useAuth } from '../context/AuthContext';

const Loans: React.FC = () => {
  const { user, hasPermission } = useAuth();

  if (!hasPermission('approve_loans')) {
    return <p>You do not have permission to view this page.</p>;
  }

  return (
    <div style={{
      padding: 20,
      maxWidth: 800,
      margin: 'auto',
      fontFamily: 'system-ui, -apple-system, sans-serif',
      color: '#1f2937',
    }}>
      <h1 style={{ fontSize: '28px', fontWeight: 'bold', marginBottom: '16px' }}>Loans Management</h1>
      <p style={{ fontSize: '16px', marginBottom: '24px' }}>Welcome, {user?.username}. Here you can manage loan requests and approvals.</p>
      {/* TODO: Implement loan request form, approval workflow, repayment tracking */}
    </div>
  );
};

export default Loans;
