// src/App.tsx

import React from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import { AuthProvider, useAuth } from './context/AuthContext';

// Importez la nouvelle page d'inscription
import SignUpPage from './components/SignUpPage'; 
import LoginPage from './components/LoginPage';
import Layout from './components/Layout';
import Dashboard from './components/Dashboard';
import Members from './components/Members';
import Contributions from './components/Contributions';
import Loans from './components/Loans';
import Sanctions from './components/Sanctions';
import Governance from './components/Governance';

const PrivateRoute = ({ children }: { children: React.ReactElement }) => {
  const { isAuthenticated } = useAuth();
  return isAuthenticated ? children : <Navigate to="/login" replace />;
};

const App: React.FC = () => {
  return (
    <AuthProvider>
      <Router>
        <Routes>
          {/* Routes publiques (sans barre latérale) */}
          <Route path="/login" element={<LoginPage />} />
          <Route path="/signup" element={<SignUpPage />} /> {/* <-- NOUVELLE ROUTE AJOUTÉE */}

          {/* Routes privées avec la barre latérale */}
          <Route 
            path="/" 
            element={ <PrivateRoute><Layout /></PrivateRoute> }
          >
            <Route index element={<Dashboard />} />
            <Route path="members" element={<Members />} />
            <Route path="contributions" element={<Contributions />} />
            <Route path="loans" element={<Loans />} />
            <Route path="sanctions" element={<Sanctions />} />
            <Route path="governance" element={<Governance />} />
          </Route>
        </Routes>
      </Router>
    </AuthProvider>
  );
};

export default App;
