// src/components/Governance.tsx - VERSION AVEC PERMISSIONS CHARTE

import React, { useState } from 'react';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlus, faCalendarAlt, faBook, faLandmark, faGavel, faHandshake, faUsers, faVoteYea } from '@fortawesome/free-solid-svg-icons';

// --- INTERFACES & DONNÉES ---
interface Rule { 
  title: string; 
  content: string; 
}

interface RuleCategory { 
  category: string; 
  icon: any; 
  rules: Rule[]; 
  requiredPermission: string;
}

interface Meeting { 
  id: string; 
  title: string; 
  date: string; 
  time: string; 
  status: 'À venir' | 'Passée' | 'En cours'; 
  type: 'Ouverture' | 'Clôture' | 'Extraordinaire' | 'Comité';
  attendees?: number;
  decisions?: string[];
}

interface Vote {
  id: string;
  title: string;
  description: string;
  type: 'Modification Charte' | 'Sanction' | 'Prêt Important' | 'Règle';
  status: 'En cours' | 'Approuvé' | 'Rejeté';
  votesFor: number;
  votesAgainst: number;
  totalVoters: number;
  requiredMajority: 'Simple' | 'Qualifiée' | 'Unanimité';
  endDate: string;
}

// Règles selon la charte avec permissions requises
const governanceRules: RuleCategory[] = [
  {
    category: "Cotisations & Fonds",
    icon: faLandmark,
    requiredPermission: "view_governance",
    rules: [
      { 
        title: "Montant Minimal (Article 3.1)", 
        content: "La contribution mensuelle minimale est de 4 000 XAF, à déposer entre le 24 et le 25 du mois." 
      },
      { 
        title: "Gestion du Fonds (Article 3.2-3.3)", 
        content: "Les cotisations sont versées sur un compte commun géré par le Trésorier et supervisé par le Comité de Gestion (3-6 membres)." 
      },
      { 
        title: "Rapports Mensuels (Article 3.4)", 
        content: "Un rapport financier détaillé est présenté mensuellement à l'ensemble des membres." 
      },
    ]
  },
  {
    category: "Système de Points Berry",
    icon: faBook,
    requiredPermission: "view_berry_calculations",
    rules: [
      { 
        title: "Attribution de base", 
        content: "Chaque contribution assidue ajoute 5 points. Un retard entraîne une pénalité de 15 points." 
      },
      { 
        title: "Bonus Contributions", 
        content: "Des bonus sont accordés : >6800 XAF (+5pts), >20% du fonds (+10pts), >50000 XAF (+20pts)." 
      },
      { 
        title: "Pénalités (Article 6.5)", 
        content: "Une absence de contribution pendant un mois entraîne une perte de 50% des points Berry acquis." 
      },
      { 
        title: "Échelle de Prêt", 
        content: "10-100pts = 60k XAF, 100-199pts = 120k XAF, 200-499pts = 300k XAF, 500+pts = selon fonds disponibles." 
      },
    ]
  },
  {
    category: "Prêts aux Membres",
    icon: faHandshake,
    requiredPermission: "view_loans",
    rules: [
      { 
        title: "Conditions d'Accès (Article 4.1)", 
        content: "L'accès au fonds est réservé aux urgences avérées (maladie, accident, etc.)." 
      },
      { 
        title: "Procédure (Article 4.2-4.3)", 
        content: "Toute demande doit être formulée par écrit et examinée par le Comité de Gestion." 
      },
      { 
        title: "Garanties (Article 4.7)", 
        content: "Chaque prêt individuel doit être soutenu par au moins deux avaliseurs membres du groupe." 
      },
      { 
        title: "Gros Prêts (Article 4.5)", 
        content: "L'approbation d'un retrait de plus de 35% du fonds doit être validée par l'ensemble des membres." 
      },
      { 
        title: "Taux d'Intérêt (Article 8.1)", 
        content: "10% (0-39999 XAF), 8% (40k-99999 XAF), 6% (100k+ XAF). Remboursement minimum 10% du capital/mois." 
      },
    ]
  },
  {
    category: "Sanctions & Exclusions",
    icon: faGavel,
    requiredPermission: "view_sanctions",
    rules: [
      { 
        title: "Retard de Cotisation (Article 6.3)", 
        content: "Sanctionné par une amende de 200 XAF par jour de retard." 
      },
      { 
        title: "Processus Décisionnel (Article 6.2)", 
        content: "Toutes les décisions disciplinaires sont prises par un vote majoritaire des membres." 
      },
      { 
        title: "Non-Remboursement (Article 6.4)", 
        content: "En cas de défaut, saisie des fonds du membre puis de ses avaliseurs." 
      },
    ]
  },
  {
    category: "Réunions & Gouvernance",
    icon: faUsers,
    requiredPermission: "view_governance",
    rules: [
      { 
        title: "Comité Exécutif (Article 5.4)", 
        content: "Le Président et le Censeur peuvent organiser des sessions extraordinaires." 
      },
      { 
        title: "Réunions Obligatoires (Article 5.5-5.6)", 
        content: "Deux sessions mensuelles obligatoires : ouverture et clôture du mois." 
      },
      { 
        title: "Modifications Charte (Article 7.2)", 
        content: "Toute modification de la charte doit être validée à l'unanimité." 
      },
    ]
  },
];

const mockMeetings: Meeting[] = [
  { 
    id: 'M01', 
    title: "Réunion de Clôture - Juin 2025", 
    date: "2025-06-30", 
    time: "20:00", 
    status: 'Passée',
    type: 'Clôture',
    attendees: 9,
    decisions: ["Validation budget juillet", "Approbation 2 prêts"]
  },
  { 
    id: 'M02', 
    title: "Réunion d'Ouverture - Juillet 2025", 
    date: "2025-07-02", 
    time: "20:00", 
    status: 'Passée',
    type: 'Ouverture',
    attendees: 8,
    decisions: ["Planning réunions mois", "Nouveaux objectifs"]
  },
  { 
    id: 'M03', 
    title: "Session Extraordinaire - Modification Règle", 
    date: "2025-07-20", 
    time: "19:00", 
    status: 'En cours',
    type: 'Extraordinaire',
    attendees: 10
  },
  { 
    id: 'M04', 
    title: "Réunion de Clôture - Juillet 2025", 
    date: "2025-07-31", 
    time: "20:00", 
    status: 'À venir',
    type: 'Clôture'
  },
];

const mockVotes: Vote[] = [
  {
    id: 'V001',
    title: "Modification seuil bonus Berry",
    description: "Proposition d'abaisser le seuil du bonus de 6800 XAF à 6000 XAF",
    type: 'Modification Charte',
    status: 'En cours',
    votesFor: 7,
    votesAgainst: 2,
    totalVoters: 10,
    requiredMajority: 'Unanimité',
    endDate: '2025-07-25'
  },
  {
    id: 'V002',
    title: "Prêt exceptionnel Diana Prince",
    description: "Prêt de 45% du fonds pour urgence médicale familiale",
    type: 'Prêt Important',
    status: 'En cours',
    votesFor: 8,
    votesAgainst: 1,
    totalVoters: 10,
    requiredMajority: 'Simple',
    endDate: '2025-07-22'
  },
];

// --- COMPOSANT PRINCIPAL ---
const Governance: React.FC = () => {
  const { user, hasPermission, getRoleDisplayName } = useAuth();
  const navigate = useNavigate();
  const [meetings, setMeetings] = useState<Meeting[]>(mockMeetings);
  const [votes, setVotes] = useState<Vote[]>(mockVotes);
  const [votedItems, setVotedItems] = useState<string[]>([]);

  // --- VÉRIFICATION DES PERMISSIONS ---
  if (!hasPermission('view_governance')) {
    return (
      <div style={styles.page}>
        <div style={styles.errorContainer}>
          <h3>Accès refusé</h3>
          <p>Vous n'avez pas la permission de consulter la gouvernance.</p>
          <p>Votre rôle actuel : <strong>{user?.role ? getRoleDisplayName(user.role as any) : 'Non défini'}</strong></p>
        </div>
      </div>
    );
  }

  // --- GESTIONNAIRES D'ÉVÉNEMENTS ---
  const handleVote = (voteId: string, choice: 'for' | 'against') => {
    if (!hasPermission('participate_in_votes')) {
      alert("Vous n'avez pas la permission de voter.");
      return;
    }

    if (votedItems.includes(voteId)) {
      alert("Vous avez déjà voté sur cette proposition.");
      return;
    }

    setVotes(votes.map(vote => 
      vote.id === voteId ? {
        ...vote,
        votesFor: choice === 'for' ? vote.votesFor + 1 : vote.votesFor,
        votesAgainst: choice === 'against' ? vote.votesAgainst + 1 : vote.votesAgainst
      } : vote
    ));

    setVotedItems([...votedItems, voteId]);
  };

  const handleScheduleMeeting = () => {
    if (!hasPermission('organize_sessions') && !hasPermission('manage_governance')) {
      alert("Vous n'avez pas la permission d'organiser des réunions.");
      return;
    }
    
    // Ici, logique pour planifier une réunion
    alert("Fonctionnalité de planification de réunion à implémenter");
  };

  // --- FILTRAGE DES RÈGLES SELON LES PERMISSIONS ---
  const visibleRules = governanceRules.filter(category => 
    hasPermission(category.requiredPermission)
  );

  // --- PERMISSIONS D'AFFICHAGE ---
  const canViewMeetings = hasPermission('view_governance') || hasPermission('organize_sessions');
  const canOrganizeMeetings = hasPermission('organize_sessions') || hasPermission('manage_governance');
  const canParticipateInVotes = hasPermission('participate_in_votes') || hasPermission('manage_governance');

  return (
    <div style={styles.page}>
      <header style={styles.header}>
        <h2 style={styles.headerTitle}>Gouvernance & Réunions</h2>
        <button onClick={() => navigate('/')} style={{...styles.button, backgroundColor: '#6b7280'}}>
          ← Retour
        </button>
      </header>

      {/* Section des règles de la charte */}
      {visibleRules.length > 0 && (
        <section>
          <h3 style={styles.sectionTitle}>Règles de la Charte (Version 5)</h3>
          <div style={styles.rulesGrid}>
            {visibleRules.map(category => (
              <div key={category.category} style={styles.card}>
                <h4 style={styles.cardTitle}>
                  <FontAwesomeIcon icon={category.icon} style={{marginRight: '10px', color: '#3b82f6'}}/>
                  {category.category}
                </h4>
                <ul style={styles.rulesList}>
                  {category.rules.map(rule => (
                    <li key={rule.title}>
                      <strong>{rule.title}:</strong> {rule.content}
                    </li>
                  ))}
                </ul>
              </div>
            ))}
          </div>
        </section>
      )}

      {/* Section des votes en cours */}
      {canParticipateInVotes && votes.filter(v => v.status === 'En cours').length > 0 && (
        <section>
          <h3 style={styles.sectionTitle}>Votes en Cours</h3>
          <div style={styles.votesContainer}>
            {votes.filter(v => v.status === 'En cours').map(vote => (
              <div key={vote.id} style={styles.voteCard}>
                <div style={styles.voteHeader}>
                  <h4 style={styles.voteTitle}>{vote.title}</h4>
                  <TypeChip type={vote.type} />
                </div>
                <p style={styles.voteDescription}>{vote.description}</p>
                <div style={styles.voteStats}>
                  <span>Pour: <strong style={{color: '#22c55e'}}>{vote.votesFor}</strong></span>
                  <span>Contre: <strong style={{color: '#ef4444'}}>{vote.votesAgainst}</strong></span>
                  <span>Total: {vote.totalVoters} votants</span>
                  <span>Requis: {vote.requiredMajority}</span>
                </div>
                <div style={styles.voteActions}>
                  <span style={{fontSize: '12px', color: '#6b7280'}}>
                    Fin: {new Date(vote.endDate).toLocaleDateString('fr-FR')}
                  </span>
                  {!votedItems.includes(vote.id) ? (
                    <div style={styles.voteButtons}>
                      <button 
                        onClick={() => handleVote(vote.id, 'for')}
                        style={{...styles.button, backgroundColor: '#22c55e', fontSize: '12px'}}
                      >
                        <FontAwesomeIcon icon={faVoteYea} style={{marginRight: '4px'}} />
                        Pour
                      </button>
                      <button 
                        onClick={() => handleVote(vote.id, 'against')}
                        style={{...styles.button, backgroundColor: '#ef4444', fontSize: '12px'}}
                      >
                        Contre
                      </button>
                    </div>
                  ) : (
                    <span style={{color: '#22c55e', fontSize: '12px', fontWeight: 'bold'}}>✓ Voté</span>
                  )}
                </div>
              </div>
            ))}
          </div>
        </section>
      )}

      {/* Section du calendrier des réunions */}
      {canViewMeetings && (
        <section>
          <div style={{...styles.header, marginTop: '40px', marginBottom: '24px'}}>
            <h3 style={styles.sectionTitle}>Calendrier des Réunions</h3>
            {canOrganizeMeetings && (
              <button onClick={handleScheduleMeeting} style={styles.button}>
                <FontAwesomeIcon icon={faPlus} style={{marginRight: '8px'}} /> 
                Planifier une Réunion
              </button>
            )}
          </div>

          <div style={styles.meetingsList}>
            {meetings.map(meeting => (
              <div key={meeting.id} style={{...styles.card, ...styles.meetingCard}}>
                <div style={styles.meetingInfo}>
                  <div style={styles.meetingHeader}>
                    <p style={styles.meetingTitle}>{meeting.title}</p>
                    <div style={styles.meetingBadges}>
                      <MeetingTypeChip type={meeting.type} />
                      <StatusChip status={meeting.status} />
                    </div>
                  </div>
                  <p style={styles.meetingDate}>
                    <FontAwesomeIcon icon={faCalendarAlt} style={{marginRight: '8px'}}/>
                    {new Date(meeting.date).toLocaleDateString('fr-FR', {
                      weekday: 'long', 
                      year: 'numeric', 
                      month: 'long', 
                      day: 'numeric'
                    })} à {meeting.time}
                  </p>
                  {meeting.attendees && (
                    <p style={styles.meetingAttendees}>
                      👥 {meeting.attendees} participants
                    </p>
                  )}
                  {meeting.decisions && meeting.decisions.length > 0 && (
                    <div style={styles.meetingDecisions}>
                      <strong>Décisions prises :</strong>
                      <ul style={styles.decisionsList}>
                        {meeting.decisions.map((decision, index) => (
                          <li key={index}>{decision}</li>
                        ))}
                      </ul>
                    </div>
                  )}
                </div>
              </div>
            ))}
          </div>
        </section>
      )}

      {/* Information sur les permissions */}
      <div style={styles.permissionInfo}>
        <h4>Vos permissions actuelles :</h4>
        <div style={styles.permissionsList}>
          {hasPermission('view_governance') && <span style={styles.permissionBadge}>📋 Consulter gouvernance</span>}
          {hasPermission('participate_in_votes') && <span style={styles.permissionBadge}>🗳️ Participer aux votes</span>}
          {hasPermission('organize_sessions') && <span style={styles.permissionBadge}>📅 Organiser réunions</span>}
          {hasPermission('manage_governance') && <span style={styles.permissionBadge}>⚖️ Gérer gouvernance</span>}
          {hasPermission('modify_charter') && <span style={styles.permissionBadge}>📜 Modifier charte</span>}
        </div>
      </div>
    </div>
  );
};

// --- SOUS-COMPOSANTS ---
const StatusChip: React.FC<{ status: Meeting['status'] }> = ({ status }) => { 
  const sC = { 
    'À venir': { backgroundColor: '#dcfce7', color: '#166534' }, 
    'En cours': { backgroundColor: '#fef3c7', color: '#92400e' }, 
    'Passée': { backgroundColor: '#e5e7eb', color: '#4b5563' } 
  }; 
  return <span style={{ ...styles.chip, ...sC[status] }}>{status}</span>; 
};

const MeetingTypeChip: React.FC<{ type: Meeting['type'] }> = ({ type }) => { 
  const tC = { 
    'Ouverture': { backgroundColor: '#dbeafe', color: '#1e40af' }, 
    'Clôture': { backgroundColor: '#fecaca', color: '#991b1b' }, 
    'Extraordinaire': { backgroundColor: '#f3e8ff', color: '#7c2d12' }, 
    'Comité': { backgroundColor: '#d1fae5', color: '#065f46' } 
  }; 
  return <span style={{ ...styles.chip, ...tC[type] }}>{type}</span>; 
};

const TypeChip: React.FC<{ type: Vote['type'] }> = ({ type }) => { 
  const tC = { 
    'Modification Charte': { backgroundColor: '#fef3c7', color: '#92400e' }, 
    'Sanction': { backgroundColor: '#fee2e2', color: '#991b1b' }, 
    'Prêt Important': { backgroundColor: '#dbeafe', color: '#1e40af' }, 
    'Règle': { backgroundColor: '#e0e7ff', color: '#3730a3' } 
  }; 
  return <span style={{ ...styles.chip, ...tC[type] }}>{type}</span>; 
};

// --- STYLES ---
const styles: { [key: string]: React.CSSProperties } = {
  page: { fontFamily: 'Arial, sans-serif', padding: '16px', backgroundColor: '#f3f4f6', minHeight: '100vh' },
  header: { display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '24px', flexWrap: 'wrap', gap: '16px' },
  headerTitle: { fontSize: '24px', fontWeight: 'bold', color: '#1f2937', margin: 0 },
  sectionTitle: { fontSize: '22px', fontWeight: 'bold', color: '#1f2937', margin: 0 },
  errorContainer: { textAlign: 'center', padding: '40px', backgroundColor: 'white', borderRadius: '12px', boxShadow: '0 4px 12px rgba(0, 0, 0, 0.08)' },
  button: { padding: '10px 16px', backgroundColor: '#2563eb', color: 'white', border: 'none', borderRadius: '8px', cursor: 'pointer', fontWeight: '600', display: 'flex', alignItems: 'center', justifyContent: 'center', fontSize: '14px' },
  rulesGrid: { display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(320px, 1fr))', gap: '24px', marginTop: '24px', marginBottom: '40px' },
  card: { backgroundColor: 'white', padding: '24px', borderRadius: '12px', boxShadow: '0 4px 12px rgba(0, 0, 0, 0.08)' },
  cardTitle: { fontSize: '18px', fontWeight: 'bold', margin: '0 0 16px 0', color: '#1f2937', borderBottom: '1px solid #e5e7eb', paddingBottom: '12px', display: 'flex', alignItems: 'center' },
  rulesList: { margin: 0, paddingLeft: '20px', display: 'flex', flexDirection: 'column', gap: '12px', fontSize: '14px', color: '#374151', lineHeight: 1.6 },
  votesContainer: { display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))', gap: '16px', marginTop: '16px', marginBottom: '40px' },
  voteCard: { backgroundColor: 'white', padding: '20px', borderRadius: '12px', boxShadow: '0 4px 12px rgba(0, 0, 0, 0.08)', borderLeft: '4px solid #3b82f6' },
  voteHeader: { display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', marginBottom: '12px' },
  voteTitle: { fontSize: '16px', fontWeight: 'bold', margin: 0, color: '#1f2937' },
  voteDescription: { fontSize: '14px', color: '#4b5563', marginBottom: '16px' },
  voteStats: { display: 'flex', gap: '16px', fontSize: '12px', marginBottom: '16px', flexWrap: 'wrap' },
  voteActions: { display: 'flex', justifyContent: 'space-between', alignItems: 'center' },
  voteButtons: { display: 'flex', gap: '8px' },
  meetingsList: { display: 'flex', flexDirection: 'column', gap: '16px', marginBottom: '40px' },
  meetingCard: { display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', flexWrap: 'wrap', gap: '16px' },
  meetingInfo: { flex: 1 },
  meetingHeader: { display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', marginBottom: '8px' },
  meetingBadges: { display: 'flex', gap: '8px', flexWrap: 'wrap' },
  meetingTitle: { fontWeight: 'bold', margin: 0, color: '#1f2937', fontSize: '16px' },
  meetingDate: { fontSize: '14px', margin: '4px 0', color: '#4b5563' },
  meetingAttendees: { fontSize: '12px', margin: '4px 0', color: '#6b7280' },
  meetingDecisions: { marginTop: '12px', fontSize: '12px', color: '#374151' },
  decisionsList: { marginTop: '4px', paddingLeft: '16px' },
  permissionInfo: { backgroundColor: '#f0f9ff', padding: '16px', borderRadius: '8px', marginTop: '24px' },
  permissionsList: { display: 'flex', gap: '8px', flexWrap: 'wrap', marginTop: '8px' },
  permissionBadge: { backgroundColor: '#e0f2fe', padding: '4px 8px', borderRadius: '12px', fontSize: '12px', fontWeight: '500' },
  chip: { padding: '4px 12px', borderRadius: '9999px', fontWeight: '600', fontSize: '12px', flexShrink: 0 },
};

export default Governance;
