// src/components/Sanctions.tsx - VERSION AVEC PERMISSIONS CHARTE

import React, { useState, useEffect, ChangeEvent, ReactNode, SelectHTMLAttributes, TextareaHTMLAttributes } from 'react';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faGavel, faCheck, faTimes } from '@fortawesome/free-solid-svg-icons';

// --- HOOK POUR LA RESPONSIVITÉ ---
const useWindowSize = () => {
  const [size, setSize] = useState([window.innerWidth]);
  useEffect(() => {
    const handleResize = () => setSize([window.innerWidth]);
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);
  return { width: size[0] };
};

// --- INTERFACES & DONNÉES ---
interface Sanction { 
  id: string; 
  memberId: string; 
  memberName: string; 
  type: 'Avertissement' | 'Amende' | 'Exclusion'; 
  reason: string; 
  date: string; 
  status: 'Vote en cours' | 'Appliquée' | 'Rejetée'; 
  votesFor: number; 
  votesAgainst: number; 
}

interface Member { 
  id: string; 
  name: string; 
}

const initialMembers: Member[] = [
  { id: 'M001', name: 'Alice Dupont' },
  { id: 'M002', name: 'Bob Martin' },
  { id: 'M003', name: 'Charlie Brown' },
  { id: 'M004', name: 'Diana Prince' }
];

const mockSanctions: Sanction[] = [
  { 
    id: 'S001', 
    memberId: 'M002', 
    memberName: 'Bob Martin', 
    type: 'Amende', 
    reason: "Retard de 3 jours sur la contribution de Juin 2025.", 
    date: '2025-06-28', 
    status: 'Appliquée', 
    votesFor: 15, 
    votesAgainst: 2 
  },
  { 
    id: 'S002', 
    memberId: 'M004', 
    memberName: 'Diana Prince', 
    type: 'Avertissement', 
    reason: "Absence non justifiée à la réunion de clôture.", 
    date: '2025-07-01', 
    status: 'Vote en cours', 
    votesFor: 5, 
    votesAgainst: 3 
  },
  { 
    id: 'S003', 
    memberId: 'M001', 
    memberName: 'Alice Dupont', 
    type: 'Exclusion', 
    reason: "Proposition suite à un manquement grave aux engagements financiers.", 
    date: '2025-07-05', 
    status: 'Rejetée', 
    votesFor: 4, 
    votesAgainst: 18 
  },
];

// --- COMPOSANT PRINCIPAL ---
const Sanctions: React.FC = () => {
  const { user, hasPermission, getRoleDisplayName } = useAuth();
  const navigate = useNavigate();
  const { width } = useWindowSize();
  const isMobile = width < 768;

  const [sanctions, setSanctions] = useState<Sanction[]>(mockSanctions);
  const [members] = useState<Member[]>(initialMembers);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [formError, setFormError] = useState('');
  const [votedSanctions, setVotedSanctions] = useState<string[]>([]);
  
  const initialFormState = { 
    memberId: '', 
    type: 'Avertissement' as Sanction['type'], 
    reason: '' 
  };
  const [newSanction, setNewSanction] = useState(initialFormState);

  // --- VÉRIFICATION DES PERMISSIONS ---
  if (!hasPermission('view_sanctions')) {
    return (
      <div style={styles.page}>
        <div style={styles.errorContainer}>
          <h3>Accès refusé</h3>
          <p>Vous n'avez pas la permission de consulter les sanctions.</p>
          <p>Votre rôle actuel : <strong>{user?.role ? getRoleDisplayName(user.role as any) : 'Non défini'}</strong></p>
        </div>
      </div>
    );
  }

  // --- GESTIONNAIRES D'ÉVÉNEMENTS ---
  const handleVote = (id: string, vote: 'for' | 'against') => {
    // Selon la charte Article 6.2 : "Les décisions disciplinaires sont prises par un vote majoritaire des membres"
    if (!hasPermission('participate_in_votes') && !hasPermission('manage_sanctions')) {
      alert("Vous n'avez pas la permission de voter sur les sanctions.");
      return;
    }

    if (votedSanctions.includes(id)) { 
      alert("Vous avez déjà voté pour cette sanction."); 
      return; 
    }

    setSanctions(sanctions.map(s => 
      s.id === id ? 
        (vote === 'for' ? 
          { ...s, votesFor: s.votesFor + 1 } : 
          { ...s, votesAgainst: s.votesAgainst + 1 }
        ) : s
    ));
    setVotedSanctions([...votedSanctions, id]);
  };

  const handleSaveSanction = () => {
    if (!hasPermission('manage_sanctions')) {
      alert("Vous n'avez pas la permission de proposer des sanctions.");
      return;
    }

    if (!newSanction.memberId || !newSanction.reason) { 
      setFormError("Veuillez sélectionner un membre et fournir une justification."); 
      return; 
    }

    const member = members.find(m => m.id === newSanction.memberId);
    if (!member) return;

    const sanctionToAdd: Sanction = { 
      id: `S${Date.now()}`, 
      memberId: member.id, 
      memberName: member.name, 
      type: newSanction.type, 
      reason: newSanction.reason, 
      date: new Date().toISOString().split('T')[0], 
      status: 'Vote en cours', 
      votesFor: 1, // Le proposant vote automatiquement pour
      votesAgainst: 0, 
    };

    setSanctions([sanctionToAdd, ...sanctions]);
    setIsModalOpen(false);
  };
  
  const handleOpenModal = () => {
    if (!hasPermission('manage_sanctions')) {
      alert("Vous n'avez pas la permission de proposer des sanctions.");
      return;
    }

    setNewSanction(initialFormState);
    setFormError('');
    setIsModalOpen(true);
  };

  // --- STATISTIQUES POUR LES RÔLES AUTORISÉS ---
  const canViewStats = hasPermission('view_reports') || hasPermission('manage_sanctions');

  return (
    <div style={styles.page}>
      <header style={styles.header}>
        <h2 style={styles.headerTitle}>Sanctions & Exclusions</h2>
        <button onClick={() => navigate('/')} style={{...styles.button, backgroundColor: '#6b7280'}}>
          ← Retour
        </button>
      </header>

      {canViewStats && (
        <section style={styles.statsSection}>
          <div style={styles.statCard}>
            <h4>Sanctions en cours</h4>
            <p>{sanctions.filter(s => s.status === 'Vote en cours').length}</p>
          </div>
          <div style={styles.statCard}>
            <h4>Sanctions appliquées</h4>
            <p>{sanctions.filter(s => s.status === 'Appliquée').length}</p>
          </div>
          <div style={styles.statCard}>
            <h4>Sanctions rejetées</h4>
            <p>{sanctions.filter(s => s.status === 'Rejetée').length}</p>
          </div>
        </section>
      )}

      <section style={styles.controlsSection}>
        {hasPermission('manage_sanctions') && (
          <button onClick={handleOpenModal} style={styles.button}>
            <FontAwesomeIcon icon={faGavel} style={{ marginRight: '8px' }} /> 
            Proposer une Sanction
          </button>
        )}
        
        {!hasPermission('manage_sanctions') && hasPermission('participate_in_votes') && (
          <div style={styles.infoBox}>
            <p>Vous pouvez voter sur les sanctions proposées par le comité de gestion.</p>
          </div>
        )}
      </section>

      {isMobile ? 
        <MobileSanctionList 
          sanctions={sanctions} 
          onVote={handleVote} 
          votedSanctions={votedSanctions}
          hasPermission={hasPermission}
        /> : 
        <DesktopSanctionTable 
          sanctions={sanctions} 
          onVote={handleVote} 
          votedSanctions={votedSanctions}
          hasPermission={hasPermission}
        />
      }

      {isModalOpen && (
        <div style={styles.modalOverlay}>
          <div style={styles.modalContent}>
            <h3 style={styles.modalTitle}>Proposer une Sanction</h3>
            {formError && <p style={styles.formError}>{formError}</p>}
            <div style={styles.modalForm}>
              <ModalSelect 
                label="Membre concerné" 
                value={newSanction.memberId} 
                onChange={(e: ChangeEvent<HTMLSelectElement>) => 
                  setNewSanction({...newSanction, memberId: e.target.value})
                }
              >
                <option value="" disabled>Sélectionner un membre...</option>
                {members.map(m => 
                  <option key={m.id} value={m.id}>{m.name}</option>
                )}
              </ModalSelect>
              
              <ModalSelect 
                label="Type de sanction" 
                value={newSanction.type} 
                onChange={(e: ChangeEvent<HTMLSelectElement>) => 
                  setNewSanction({...newSanction, type: e.target.value as Sanction['type']})
                }
              >
                <option value="Avertissement">Avertissement</option>
                <option value="Amende">Amende (200 XAF/jour de retard)</option>
                <option value="Exclusion">Exclusion</option>
              </ModalSelect>
              
              <ModalTextarea 
                label="Justification / Motif" 
                value={newSanction.reason} 
                onChange={(e: ChangeEvent<HTMLTextAreaElement>) => 
                  setNewSanction({...newSanction, reason: e.target.value})
                } 
                placeholder="Décrivez clairement le manquement selon la charte..."
              />
              
              <div style={styles.charterReminder}>
                <h4>Rappel de la charte :</h4>
                <ul>
                  <li>Retard de contribution : 200 XAF par jour</li>
                  <li>Absence d'un mois : perte de 50% des points Berry</li>
                  <li>Vote majoritaire requis pour appliquer la sanction</li>
                </ul>
              </div>
            </div>
            <div style={styles.modalActions}>
              <button 
                onClick={() => setIsModalOpen(false)} 
                style={{...styles.button, backgroundColor: '#6b7280'}}
              >
                Annuler
              </button>
              <button onClick={handleSaveSanction} style={styles.button}>
                Soumettre au Vote
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

