                  <button onClick={() => onStatusUpdate(loan.id, 'Rejeté')} style={{...styles.actionButton, backgroundColor: '#ef4444'}} title="Rejeter"><FontAwesomeIcon icon={faTimes} /><span style={styles.actionButtonText}>Rejeter</span></button>
                </div>
              )}
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  </div>
);
const MobileLoanList: React.FC<{loans: Loan[], hasPermission: any, onStatusUpdate: (id: string, status: 'Approuvé' | 'Rejeté') => void}> = ({ loans, hasPermission, onStatusUpdate }) => (
    <div style={{display: 'flex', flexDirection: 'column', gap: '16px'}}>
        {loans.map(loan => (
            <div key={loan.id} style={styles.mobileCard}>
                <div style={styles.mobileCardHeader}><span style={{fontWeight: 'bold', color: '#1f2937'}}>{loan.memberName}</span><StatusChip status={loan.status} /></div>
                <div style={styles.mobileCardBody}>
                    <div style={styles.mobileCardRow}><span>Montant:</span> <strong>{loan.amount.toLocaleString()} XAF</strong></div>
                    <div style={styles.mobileCardRow}><span>Total à rembourser:</span> <strong>{loan.totalToRepay.toLocaleString()} XAF</strong></div>
                    <div style={styles.mobileCardRow}><span>Date:</span> <span>{new Date(loan.date).toLocaleDateString('fr-FR')}</span></div>
                </div>
                {loan.status === 'En attente' && hasPermission('approve_loans') && (
                    <div style={styles.mobileCardFooter}>
                        <button onClick={() => onStatusUpdate(loan.id, 'Approuvé')} style={{...styles.button, flex: 1, backgroundColor: '#22c55e'}}><FontAwesomeIcon icon={faCheck} style={{marginRight: '8px'}}/> Approuver</button>
                        <button onClick={() => onStatusUpdate(loan.id, 'Rejeté')} style={{...styles.button, flex: 1, backgroundColor: '#ef4444'}}><FontAwesomeIcon icon={faTimes} style={{marginRight: '8px'}}/> Rejeter</button>
                    </div>
                )}
            </div>
        ))}
    </div>
);

// --- SOUS-COMPOSANTS & STYLES ---
const StatCard: React.FC<{ title: string; value: string | number }> = ({ title, value }) => (<div style={{...styles.card, flex: '1 1 250px', textAlign: 'center' }}><h4 style={styles.statTitle}>{title}</h4><p style={styles.statValue}>{value}</p></div>);
const StatusChip: React.FC<{ status: Loan['status'] }> = ({ status }) => { const sC = { 'En attente': { backgroundColor: '#fef3c7', color: '#92400e' }, 'Approuvé': { backgroundColor: '#dcfce7', color: '#166534' }, 'Rejeté': { backgroundColor: '#fee2e2', color: '#991b1b' }, 'Remboursé': { backgroundColor: '#e0e7ff', color: '#3730a3' } }; return <span style={{ ...styles.chip, ...sC[status] }}>{status}</span>; };
interface ModalInputProps extends InputHTMLAttributes<HTMLInputElement> { label: string; }
const ModalInput: React.FC<ModalInputProps> = ({ label, ...props }) => (<div><label style={styles.modalLabel}>{label}</label><input style={styles.modalInput} {...props} /></div>);
interface ModalSelectProps extends SelectHTMLAttributes<HTMLSelectElement> { label: string; children: ReactNode; }
const ModalSelect: React.FC<ModalSelectProps> = ({ label, children, ...props }) => (<div><label style={styles.modalLabel}>{label}</label><select style={styles.modalInput} {...props}>{children}</select></div>);
interface ModalTextareaProps extends TextareaHTMLAttributes<HTMLTextAreaElement> { label: string; }
const ModalTextarea: React.FC<ModalTextareaProps> = ({ label, ...props }) => (<div><label style={styles.modalLabel}>{label}</label><textarea style={{...styles.modalInput, height: '80px'}} {...props} /></div>);

