// src/components/Profile.tsx

import React, { useState, useEffect, ReactNode, InputHTMLAttributes } from 'react';
import { useAuth } from '../context/AuthContext';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser, faEnvelope, faPhone, faLock, faSave, faIdCard, faStar, faWallet } from '@fortawesome/free-solid-svg-icons';

// --- INTERFACES & CONSTANTES ---
// CORRECTION: Ajout du rôle 'Admin' à la liste des types possibles
type MemberRole = 'President' | 'Treasurer' | 'Secrecom' | 'Accountant' | 'Censor' | 'Member' | 'Guest' | 'Admin';
type MemberStatus = 'Actif' | 'Suspendu' | 'Exclu';
interface Member {
  id: string;
  personalInfo: { firstName: string; lastName: string; email: string; phone: string; };
  accountInfo: { role: MemberRole; joinDate: string; status: MemberStatus; };
  berryPoints: { totalPoints: number; eligibilityLevel: 'Bronze' | 'Silver' | 'Gold' | 'Platinum'; };
  financial: { totalContributions: number; loanEligibility: number; };
}

// Données de simulation (maintenant valides avec le type corrigé)
const initialMembersData: Member[] = [
    { id: 'M001', personalInfo: { firstName: 'Alice', lastName: 'Dupont', email: 'alice.dupont@example.com', phone: '+237699887766' }, accountInfo: { role: 'Admin', joinDate: '2022-01-01', status: 'Actif' }, berryPoints: { totalPoints: 450, eligibilityLevel: 'Gold' }, financial: { totalContributions: 125000, loanEligibility: 300000 } },
    { id: 'M002', personalInfo: { firstName: 'Bob', lastName: 'Martin', email: 'bob.martin@example.com', phone: '+237677665544' }, accountInfo: { role: 'Member', joinDate: '2022-03-15', status: 'Actif' }, berryPoints: { totalPoints: 320, eligibilityLevel: 'Gold' }, financial: { totalContributions: 95000, loanEligibility: 300000 } }
];

// --- COMPOSANT PRINCIPAL ---
const Profile: React.FC = () => {
    const { user } = useAuth();
    const [member, setMember] = useState<Member | null>(null);
    const [loading, setLoading] = useState(true);
    const [message, setMessage] = useState('');

    useEffect(() => {
        if (user) {
            const fullProfile = initialMembersData.find(m => m.id === user.id);
            setMember(fullProfile || null);
        }
        setLoading(false);
    }, [user]);

    const handleProfileSubmit = (e: React.FormEvent) => {
        e.preventDefault();
        console.log('Updating profile...');
        setMessage('Profil mis à jour avec succès !');
        setTimeout(() => setMessage(''), 3000);
    };

    const handlePasswordSubmit = (e: React.FormEvent) => {
        e.preventDefault();
        console.log('Changing password...');
        setMessage('Mot de passe mis à jour avec succès !');
        setTimeout(() => setMessage(''), 3000);
    };

    if (loading) return <p>Chargement du profil...</p>;
    if (!member) return <p>Impossible de charger les informations du profil.</p>;

    return (
        <>
            <header style={styles.header}>
                <h2 style={styles.headerTitle}>Mon Profil</h2>
            </header>

            {message && <div style={styles.successMessage}>{message}</div>}

            <div style={styles.grid}>
                <ProfileHeaderCard member={member} />
                <InfoCard icon={faStar} title="Mes Points Berry">
                    <InfoRow label="Solde Total" value={`${member.berryPoints.totalPoints} points`} />
                    <InfoRow label="Niveau d'Éligibilité" value={<LevelChip level={member.berryPoints.eligibilityLevel} />} />
                </InfoCard>
                <InfoCard icon={faWallet} title="Mon Résumé Financier">
                    <InfoRow label="Total des Contributions" value={`${member.financial.totalContributions.toLocaleString()} XAF`} />
                    <InfoRow label="Pouvoir d'Emprunt Actuel" value={`${member.financial.loanEligibility.toLocaleString()} XAF`} />
                </InfoCard>
                <InfoCard icon={faUser} title="Modifier mes Informations">
                    <form onSubmit={handleProfileSubmit} style={styles.form}>
                        <InputField label="Email" name="email" type="email" defaultValue={member.personalInfo.email} icon={faEnvelope} />
                        <InputField label="Téléphone" name="phone" type="tel" defaultValue={member.personalInfo.phone} icon={faPhone} />
                        <button type="submit" style={styles.button}><FontAwesomeIcon icon={faSave} style={{ marginRight: '8px' }} /> Enregistrer</button>
                    </form>
                </InfoCard>
                <InfoCard icon={faLock} title="Changer le Mot de Passe">
                    <form onSubmit={handlePasswordSubmit} style={styles.form}>
                        <InputField label="Mot de passe actuel" name="current" type="password" icon={faLock} />
                        <InputField label="Nouveau mot de passe" name="new" type="password" icon={faLock} />
                        <button type="submit" style={styles.button}>Mettre à jour</button>
                    </form>
                </InfoCard>
            </div>
        </>
    );
};

// --- SOUS-COMPOSANTS ---
const ProfileHeaderCard: React.FC<{ member: Member }> = ({ member }) => (
    <div style={{...styles.card, ...styles.profileHeaderCard}}>
        <div style={styles.avatar}>
            {member.personalInfo.firstName.charAt(0)}{member.personalInfo.lastName.charAt(0)}
        </div>
        <h3 style={styles.profileName}>{member.personalInfo.firstName} {member.personalInfo.lastName}</h3>
        <div style={styles.profileChips}>
            <RoleChip role={member.accountInfo.role} />
            <StatusChip status={member.accountInfo.status} />
        </div>
        <p style={styles.joinDate}>Membre depuis le {new Date(member.accountInfo.joinDate).toLocaleDateString('fr-FR')}</p>
    </div>
);
const InfoCard: React.FC<{ icon: any, title: string, children: ReactNode }> = ({ icon, title, children }) => ( <div style={styles.card}> <h3 style={styles.cardTitle}><FontAwesomeIcon icon={icon} style={{ marginRight: '10px', color: '#3b82f6' }}/>{title}</h3> {children} </div> );
const InfoRow: React.FC<{ label: string, value: string | ReactNode }> = ({ label, value }) => ( <div style={styles.infoRow}> <span style={styles.infoLabel}>{label}</span> <span style={styles.infoValue}>{value}</span> </div> );
const InputField: React.FC<any> = ({ label, icon, ...props }) => ( <div style={{ marginBottom: '16px' }}> <label style={styles.label}>{label}</label> <div style={{ position: 'relative' }}> <FontAwesomeIcon icon={icon} style={styles.inputIcon} /> <input style={styles.input} {...props} /> </div> </div> );
const StatusChip: React.FC<{ status: MemberStatus }> = ({ status }) => { const sC = { 'Actif': { backgroundColor: '#dcfce7', color: '#166534' }, 'Suspendu': { backgroundColor: '#fef3c7', color: '#92400e' }, 'Exclu': { backgroundColor: '#fee2e2', color: '#991b1b' } }; return <span style={{ ...styles.chip, ...sC[status] }}>{status}</span>; };

// CORRECTION: Ajout du rôle 'Admin' à l'objet des couleurs
const RoleChip: React.FC<{ role: MemberRole }> = ({ role }) => {
    const rC: Record<MemberRole, { bg: string, text: string }> = {
        'Admin': { bg: '#e0e7ff', text: '#3730a3' },
        'President': { bg: '#dbeafe', text: '#1e40af' },
        'Treasurer': { bg: '#dcfce7', text: '#166534' },
        'Secrecom': { bg: '#e5e7eb', text: '#374151' },
        'Accountant': { bg: '#d1fae5', text: '#065f46' },
        'Censor': { bg: '#fef3c7', text: '#92400e' },
        'Member': { bg: '#f3f4f6', text: '#374151' },
        'Guest': { bg: '#e5e7eb', text: '#6b7280' }
    };
    const style = rC[role] || { bg: '#e5e7eb', text: '#374151' };
    return <span style={{ ...styles.chip, backgroundColor: style.bg, color: style.text }}>{role}</span>;
};

const LevelChip: React.FC<{ level: 'Bronze' | 'Silver' | 'Gold' | 'Platinum' }> = ({ level }) => { const lC = { 'Platinum': '#f3e8ff', 'Gold': '#fef3c7', 'Silver': '#f1f5f9', 'Bronze': '#fef2f2' }; return <span style={{ ...styles.chip, backgroundColor: lC[level], color: '#374151' }}>{level}</span>; };

