// src/components/Settings.tsx

import React, { useState } from 'react';
import { useAuth } from '../context/AuthContext';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser, faEnvelope, faPhone, faLock, faSave } from '@fortawesome/free-solid-svg-icons';

const Settings: React.FC = () => {
    const { user } = useAuth();

    // Simuler les données de l'utilisateur actuel
    const [profile, setProfile] = useState({
        firstName: user?.username.split(' ')[0] || 'Alice',
        lastName: user?.username.split(' ')[1] || 'Dupont',
        email: 'alice.dupont@example.com',
        phone: '+237699887766',
    });

    const [password, setPassword] = useState({
        current: '',
        new: '',
        confirm: '',
    });

    const [message, setMessage] = useState('');

    const handleProfileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        setProfile({ ...profile, [e.target.name]: e.target.value });
    };

    const handlePasswordChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        setPassword({ ...password, [e.target.name]: e.target.value });
    };

    const handleProfileSubmit = (e: React.FormEvent) => {
        e.preventDefault();
        // Logique de l'API pour mettre à jour le profil
        console.log('Updating profile:', profile);
        setMessage('Profil mis à jour avec succès !');
        setTimeout(() => setMessage(''), 3000);
    };

    const handlePasswordSubmit = (e: React.FormEvent) => {
        e.preventDefault();
        if (password.new !== password.confirm) {
            setMessage('Les nouveaux mots de passe ne correspondent pas.');
            return;
        }
        if (password.new.length < 8) {
            setMessage('Le nouveau mot de passe doit faire au moins 8 caractères.');
            return;
        }
        // Logique de l'API pour mettre à jour le mot de passe
        console.log('Changing password...');
        setMessage('Mot de passe mis à jour avec succès !');
        setTimeout(() => setMessage(''), 3000);
    };

    return (
        <>
            <header style={styles.header}>
                <h2 style={styles.headerTitle}>Paramètres du Compte</h2>
            </header>

            {message && <div style={styles.successMessage}>{message}</div>}

            <div style={styles.grid}>
                {/* Section Informations Personnelles */}
                <div style={styles.card}>
                    <h3 style={styles.cardTitle}>Informations Personnelles</h3>
                    <form onSubmit={handleProfileSubmit} style={styles.form}>
                        <InputField label="Prénom" name="firstName" value={profile.firstName} onChange={handleProfileChange} icon={faUser} />
                        <InputField label="Nom" name="lastName" value={profile.lastName} onChange={handleProfileChange} icon={faUser} />
                        <InputField label="Email" name="email" type="email" value={profile.email} onChange={handleProfileChange} icon={faEnvelope} />
                        <InputField label="Téléphone" name="phone" type="tel" value={profile.phone} onChange={handleProfileChange} icon={faPhone} />
                        <button type="submit" style={styles.button}>
                            <FontAwesomeIcon icon={faSave} style={{ marginRight: '8px' }} />
                            Enregistrer les Modifications
                        </button>
                    </form>
                </div>

                {/* Section Mot de Passe */}
                <div style={styles.card}>
                    <h3 style={styles.cardTitle}>Changer le Mot de Passe</h3>
                    <form onSubmit={handlePasswordSubmit} style={styles.form}>
                        <InputField label="Mot de passe actuel" name="current" type="password" value={password.current} onChange={handlePasswordChange} icon={faLock} />
                        <InputField label="Nouveau mot de passe" name="new" type="password" value={password.new} onChange={handlePasswordChange} icon={faLock} />
                        <InputField label="Confirmer le nouveau mot de passe" name="confirm" type="password" value={password.confirm} onChange={handlePasswordChange} icon={faLock} />
                        <button type="submit" style={styles.button}>
                            Changer le Mot de Passe
                        </button>
                    </form>
                </div>
            </div>
        </>
    );
};

// Sous-composant pour les champs
const InputField: React.FC<any> = ({ label, icon, ...props }) => (
    <div style={{ marginBottom: '16px' }}>
        <label style={styles.label}>{label}</label>
        <div style={{ position: 'relative' }}>
            <FontAwesomeIcon icon={icon} style={styles.inputIcon} />
            <input style={styles.input} {...props} />
        </div>
    </div>
);

const styles: { [key: string]: React.CSSProperties } = {
    header: { marginBottom: '32px' },
    headerTitle: { fontSize: '28px', fontWeight: 'bold', color: '#1f2937', margin: 0 },
    grid: { display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(350px, 1fr))', gap: '24px' },
    card: { backgroundColor: 'white', padding: '24px', borderRadius: '12px', boxShadow: '0 4px 12px rgba(0, 0, 0, 0.08)' },
    cardTitle: { fontSize: '20px', fontWeight: 'bold', color: '#1f2937', margin: '0 0 24px 0', borderBottom: '1px solid #e5e7eb', paddingBottom: '12px' },
    form: { display: 'flex', flexDirection: 'column' },
    label: { display: 'block', marginBottom: '8px', fontWeight: '600', color: '#374151', fontSize: '14px' },
    input: { width: '100%', padding: '10px 10px 10px 40px', border: '1px solid #d1d5db', borderRadius: '8px', boxSizing: 'border-box', fontSize: '16px' },
    inputIcon: { position: 'absolute', top: '13px', left: '12px', color: '#9ca3af' },
    button: { padding: '10px 16px', backgroundColor: '#2563eb', color: 'white', border: 'none', borderRadius: '8px', cursor: 'pointer', fontWeight: '600', display: 'flex', alignItems: 'center', justifyContent: 'center', fontSize: '14px', marginTop: '8px' },
    successMessage: { color: '#166534', backgroundColor: '#dcfce7', padding: '12px', borderRadius: '8px', textAlign: 'center', marginBottom: '24px', fontWeight: 500 },
};

export default Settings;
