import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

const LoginPage: React.FC = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [error, setError] = useState('');
  const { login } = useAuth();
  const navigate = useNavigate();

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    const success = await login(username, password);
    if (success) {
      setError('');
      navigate('/');
    } else {
      setError('Invalid username or password');
    }
  };

  return (
    <div style={{ minHeight: '100vh', display: 'flex', fontFamily: 'system-ui, -apple-system, sans-serif' }}>
      {/* Left Section - Branding */}
      <div style={{
        flex: 1,
        background: 'linear-gradient(135deg, #3b82f6 0%, #2563eb 50%, #1d4ed8 100%)',
        position: 'relative',
        overflow: 'hidden',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        padding: '0 48px'
      }}>
        {/* Background decorative elements */}
        <div style={{ position: 'absolute', inset: 0, opacity: 0.1 }}>
          <div style={{
            position: 'absolute',
            top: '25%',
            left: '25%',
            width: '384px',
            height: '384px',
            border: '1px solid white',
            borderRadius: '50%'
          }}></div>
          <div style={{
            position: 'absolute',
            bottom: '25%',
            right: '25%',
            width: '256px',
            height: '256px',
            border: '1px solid white',
            borderRadius: '50%'
          }}></div>
        </div>

        <div style={{ position: 'relative', zIndex: 10 }}>
          <h1 style={{ fontSize: '36px', fontWeight: 'bold', color: 'white', marginBottom: '16px' }}>
            Friendly Banks
          </h1>
          <p style={{ color: 'rgba(219, 234, 254, 1)', fontSize: '18px', marginBottom: '32px', lineHeight: '1.6' }}>
            Gestion Intelligente du Fonds d'Urgence plateforme sécurisée pour 
            la gestion collaborative des ressources financières
          </p>
          <button style={{
            background: 'rgba(255, 255, 255, 0.2)',
            color: 'white',
            padding: '8px 24px',
            borderRadius: '8px',
            border: '1px solid rgba(255, 255, 255, 0.3)',
            cursor: 'pointer',
            transition: 'background-color 0.3s'
          }}
          onMouseEnter={e => (e.target as HTMLButtonElement).style.background = 'rgba(255, 255, 255, 0.3)'}
          onMouseLeave={e => (e.target as HTMLButtonElement).style.background = 'rgba(255, 255, 255, 0.2)'}>
            Learn More
          </button>
        </div>
      </div>

      {/* Right Section - Login Form */}
      <div style={{
        flex: 1,
        background: '#f9fafb',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        padding: '0 32px'
      }}>
        <div style={{ width: '100%', maxWidth: '448px' }}>
          <form onSubmit={handleSubmit} style={{
            background: 'white',
            borderRadius: '16px',
            boxShadow: '0 25px 50px -12px rgba(0, 0, 0, 0.25)',
            padding: '32px'
          }}>
            <div style={{ textAlign: 'center', marginBottom: '32px' }}>
              <h2 style={{ fontSize: '24px', fontWeight: 'bold', color: '#1f2937', marginBottom: '8px' }}>
                Welcome Back!
              </h2>
              <p style={{ color: '#6b7280' }}>Access your Friendly Banks account</p>
            </div>

            <div style={{ display: 'flex', flexDirection: 'column', gap: '24px' }}>
              <input
                id="username"
                type="text"
                value={username}
                onChange={e => setUsername(e.target.value)}
                required
                placeholder="Email Address"
                style={{
                  width: '100%',
                  padding: '16px',
                  background: '#f9fafb',
                  border: 'none',
                  borderRadius: '12px',
                  color: '#374151',
                  fontSize: '16px',
                  outline: 'none',
                  transition: 'all 0.3s',
                  boxSizing: 'border-box'
                }}
                onFocus={e => {
                  (e.target as HTMLInputElement).style.background = 'white';
                  (e.target as HTMLInputElement).style.boxShadow = '0 0 0 2px #3b82f6';
                }}
                onBlur={e => {
                  (e.target as HTMLInputElement).style.background = '#f9fafb';
                  (e.target as HTMLInputElement).style.boxShadow = 'none';
                }}
              />

              <div style={{ position: 'relative' }}>
                <input
                  id="password"
                  value={password}
                  onChange={e => setPassword(e.target.value)}
                  required
                  placeholder="Password"
                  type={showPassword ? 'text' : 'password'}
                  style={{
                    width: '100%',
                    padding: '16px',
                    background: '#f9fafb',
                    border: 'none',
                    borderRadius: '12px',
                    color: '#374151',
                    fontSize: '16px',
                    outline: 'none',
                    transition: 'all 0.3s',
                    boxSizing: 'border-box',
                    paddingRight: '48px',
                  }}
                  onFocus={e => {
                    (e.target as HTMLInputElement).style.background = 'white';
                    (e.target as HTMLInputElement).style.boxShadow = '0 0 0 2px #3b82f6';
                  }}
                  onBlur={e => {
                    (e.target as HTMLInputElement).style.background = '#f9fafb';
                    (e.target as HTMLInputElement).style.boxShadow = 'none';
                  }}
                />
                <button
                  type="button"
                  onClick={() => setShowPassword(!showPassword)}
                  style={{
                    position: 'absolute',
                    right: '16px',
                    top: '50%',
                    transform: 'translateY(-50%)',
                    background: 'none',
                    border: 'none',
                    cursor: 'pointer',
                    color: '#9ca3af',
                    fontSize: '14px',
                    padding: 0,
                    userSelect: 'none'
                  }}
                  aria-label={showPassword ? 'Hide password' : 'Show password'}
                >
                  {showPassword ? 'Hide' : 'Show'}
                </button>
              </div>

              {error && (
                <div style={{
                  background: '#fef2f2',
                  border: '1px solid #fecaca',
                  color: '#dc2626',
                  padding: '12px 16px',
                  borderRadius: '12px',
                  fontSize: '14px'
                }}>
                  {error}
                </div>
              )}

              <div style={{ textAlign: 'right', marginTop: '8px' }}>
                <button
                  type="button"
                  onClick={() => alert('Password reset functionality to be implemented')}
                  style={{
                    background: 'none',
                    border: 'none',
                    color: '#3b82f6',
                    cursor: 'pointer',
                    fontSize: '14px',
                    fontWeight: '500',
                    textDecoration: 'underline',
                    padding: 0
                  }}
                >
                  Forgot Password?
                </button>
              </div>

              <button
                type="submit"
                style={{
                  width: '100%',
                  background: '#3b82f6',
                  color: 'white',
                  fontWeight: '500',
                  padding: '16px',
                  borderRadius: '12px',
                  border: 'none',
                  cursor: 'pointer',
                  transition: 'background-color 0.3s',
                  fontSize: '16px'
                }}
                onMouseEnter={e => (e.target as HTMLButtonElement).style.background = '#2563eb'}
                onMouseLeave={e => (e.target as HTMLButtonElement).style.background = '#3b82f6'}
              >
                Login
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
