// src/components/SignUpPage.tsx

import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser, faEnvelope, faLock, faPhone } from '@fortawesome/free-solid-svg-icons';

// Interface pour les donn√©es du formulaire
interface SignUpData {
    firstName: string;
    lastName: string;
    email: string;
    phone: string;
    password: string;
    confirmPassword: string;
}

const SignUpPage: React.FC = () => {
    const navigate = useNavigate();
    const [formData, setFormData] = useState<SignUpData>({
        firstName: '', lastName: '', email: '', phone: '', password: '', confirmPassword: ''
    });
    const [errors, setErrors] = useState<Partial<SignUpData>>({});
    const [apiError, setApiError] = useState<string>('');
    const [loading, setLoading] = useState(false);

    const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        const { name, value } = e.target;
        setFormData(prev => ({ ...prev, [name]: value }));
    };

    const validateForm = (): boolean => {
        const newErrors: Partial<SignUpData> = {};
        if (!formData.firstName) newErrors.firstName = "Le pr√©nom est requis.";
        if (!formData.lastName) newErrors.lastName = "Le nom est requis.";
        if (!formData.email) {
            newErrors.email = "L'email est requis.";
        } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
            newErrors.email = "Le format de l'email est invalide.";
        }
        if (!formData.password) {
            newErrors.password = "Le mot de passe est requis.";
        } else if (formData.password.length < 8) {
            newErrors.password = "Le mot de passe doit contenir au moins 8 caract√®res.";
        }
        if (formData.password !== formData.confirmPassword) {
            newErrors.confirmPassword = "Les mots de passe –Ω–µ correspondent pas.";
        }
        setErrors(newErrors);
        return Object.keys(newErrors).length === 0;
    };

    const handleSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
        e.preventDefault();
        setApiError('');
        if (!validateForm()) return;

        setLoading(true);
        
        // --- SIMULATION D'UN APPEL API ---
        // Dans un vrai projet, vous feriez un appel √† votre backend ici.
        // ex: await fetch('/api/register', { method: 'POST', body: JSON.stringify(formData) });
        console.log("Donn√©es envoy√©es √† l'API:", formData);

        setTimeout(() => {
            // Simuler une r√©ponse de l'API
            // Pour tester une erreur, d√©commentez la ligne suivante :
            // setApiError("Un compte avec cet email existe d√©j√†."); setLoading(false); return;

            setLoading(false);
            // Rediriger vers la page de connexion avec un message de succ√®s
            navigate('/login', {
                state: { message: 'Compte cr√©√© avec succ√®s ! Veuillez vous connecter.' }
            });
        }, 1500);
    };

    return (
        <div style={styles.container}>
            <div style={styles.formWrapper}>
                <div style={styles.header}>
                    <span style={styles.logo}>üè¶</span>
                    <h1 style={styles.title}>Cr√©er un Compte</h1>
                    <p style={styles.subtitle}>Rejoignez Friendly Banks aujourd'hui.</p>
                </div>
                
                <form onSubmit={handleSubmit} style={styles.form}>
                    {apiError && <p style={styles.apiError}>{apiError}</p>}
                    
                    <div style={styles.nameContainer}>
                        <InputField name="firstName" type="text" placeholder="Pr√©nom" icon={faUser} value={formData.firstName} onChange={handleChange} error={errors.firstName} />
                        <InputField name="lastName" type="text" placeholder="Nom" icon={faUser} value={formData.lastName} onChange={handleChange} error={errors.lastName} />
                    </div>
                    <InputField name="email" type="email" placeholder="Adresse email" icon={faEnvelope} value={formData.email} onChange={handleChange} error={errors.email} />
                    <InputField name="phone" type="tel" placeholder="Num√©ro de t√©l√©phone" icon={faPhone} value={formData.phone} onChange={handleChange} error={errors.phone} />
                    <InputField name="password" type="password" placeholder="Mot de passe" icon={faLock} value={formData.password} onChange={handleChange} error={errors.password} />
                    <InputField name="confirmPassword" type="password" placeholder="Confirmer le mot de passe" icon={faLock} value={formData.confirmPassword} onChange={handleChange} error={errors.confirmPassword} />
                    
                    <button type="submit" style={styles.submitButton} disabled={loading}>
                        {loading ? 'Cr√©ation en cours...' : 'Cr√©er le compte'}
                    </button>
                </form>

                <p style={styles.footerText}>
                    Vous avez d√©j√† un compte ? <Link to="/login" style={styles.link}>Connectez-vous</Link>
                </p>
            </div>
        </div>
    );
};

// Sous-composant pour les champs de formulaire
const InputField: React.FC<any> = ({ name, icon, error, ...props }) => (
    <div style={styles.inputGroup}>
        <FontAwesomeIcon icon={icon} style={styles.inputIcon} />
        <input name={name} style={{...styles.input, ...(error && styles.inputError)}} {...props} />
        {error && <span style={styles.errorMessage}>{error}</span>}
    </div>
);

const styles: { [key: string]: React.CSSProperties } = {
    container: { display: 'flex', alignItems: 'center', justifyContent: 'center', minHeight: '100vh', backgroundColor: '#f3f4f6' },
    formWrapper: { width: '100%', maxWidth: '450px', backgroundColor: 'white', padding: '40px', borderRadius: '12px', boxShadow: '0 10px 25px rgba(0,0,0,0.1)' },
    header: { textAlign: 'center', marginBottom: '32px' },
    logo: { fontSize: '40px' },
    title: { fontSize: '28px', fontWeight: 'bold', color: '#1f2937', margin: '16px 0 8px 0' },
    subtitle: { fontSize: '16px', color: '#6b7280', margin: 0 },
    form: { display: 'flex', flexDirection: 'column', gap: '20px' },
    nameContainer: { display: 'flex', gap: '16px' },
    inputGroup: { position: 'relative', flex: 1 },
    inputIcon: { position: 'absolute', top: '13px', left: '12px', color: '#9ca3af' },
    input: { width: '100%', padding: '12px 12px 12px 40px', border: '1px solid #d1d5db', borderRadius: '8px', fontSize: '16px', boxSizing: 'border-box' },
    inputError: { borderColor: '#ef4444' },
    errorMessage: { color: '#ef4444', fontSize: '12px', marginTop: '4px', position: 'absolute' },
    apiError: { color: '#ef4444', backgroundColor: '#fee2e2', padding: '12px', borderRadius: '8px', textAlign: 'center', marginBottom: '16px' },
    submitButton: { padding: '12px', backgroundColor: '#2563eb', color: 'white', border: 'none', borderRadius: '8px', cursor: 'pointer', fontWeight: '600', fontSize: '16px', transition: 'background-color 0.2s' },
    footerText: { textAlign: 'center', marginTop: '24px', color: '#6b7280' },
    link: { color: '#2563eb', textDecoration: 'none', fontWeight: '600' },
};

export default SignUpPage;
