  mobileCardTitle: { fontWeight: 'bold', color: '#1f2937', marginRight: '8px' },
  mobileCardBody: { padding: '16px' },
  mobileCardRow: { display: 'flex', justifyContent: 'space-between', alignItems: 'center', fontSize: '14px' },
