// src/components/Governance.tsx

import React, { useState } from 'react';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlus, faCalendarAlt, faBook, faLandmark, faGavel, faHandshake } from '@fortawesome/free-solid-svg-icons';

// --- INTERFACES & DONNÉES ---
interface Rule { title: string; content: string; }
interface RuleCategory { category: string; icon: any; rules: Rule[]; }
interface Meeting { id: string; title: string; date: string; time: string; status: 'À venir' | 'Passée'; }

const governanceRules: RuleCategory[] = [
  {
    category: "Cotisations & Fonds",
    icon: faLandmark,
    rules: [
      { title: "Montant Minimal", content: "La contribution mensuelle minimale est de 4 000 XAF, à déposer entre le 24 et le 25 du mois." },
      { title: "Gestion du Fonds", content: "Les cotisations sont versées sur un compte commun géré par le Trésorier et supervisé par le Comité de Gestion (3-6 membres)." },
    ]
  },
  {
    category: "Système de Points Berry",
    icon: faBook,
    rules: [
      { title: "Attribution de base", content: "Chaque contribution assidue ajoute 5 points. Un retard entraîne une pénalité de 15 points." },
      { title: "Bonus", content: "Des bonus sont accordés pour les contributions dépassant certains seuils (ex: >6800 XAF, >20% du fonds)." },
      { title: "Absence", content: "Une absence de contribution pendant un mois entraîne une perte de 50% des points Berry acquis." },
    ]
  },
  {
    category: "Prêts aux Membres",
    icon: faHandshake,
    rules: [
        { title: "Plafond", content: "Le montant empruntable dépend du score Berry (ex: 10-100 pts = 60 000 XAF)." },
        { title: "Garantie", content: "Chaque prêt individuel doit être soutenu par au moins deux avaliseurs (garants) membres du groupe." },
    ]
  },
  {
    category: "Sanctions",
    icon: faGavel,
    rules: [
        { title: "Retard de Cotisation", content: "Sanctionné par une amende de 200 XAF par jour de retard." },
        { title: "Décisions", content: "Toutes les décisions disciplinaires (avertissement, exclusion) sont prises par un vote majoritaire des membres." },
    ]
  },
];

const mockMeetings: Meeting[] = [
  { id: 'M01', title: "Réunion de Clôture - Juin 2025", date: "2025-06-30", time: "20:00", status: 'Passée' },
  { id: 'M02', title: "Réunion d'Ouverture - Juillet 2025", date: "2025-07-02", time: "20:00", status: 'Passée' },
  { id: 'M03', title: "Réunion de Clôture - Juillet 2025", date: "2025-07-31", time: "20:00", status: 'À venir' },
];

// --- COMPOSANT PRINCIPAL ---
const Governance: React.FC = () => {
  const { user, hasPermission } = useAuth();
  const navigate = useNavigate();
  const [meetings, setMeetings] = useState<Meeting[]>(mockMeetings);

  if (!hasPermission('manage_rules') && !hasPermission('manage_meetings')) {
    return <div style={styles.page}><p>Vous n'avez pas la permission de voir cette page.</p></div>;
  }
  
  return (
    <div style={styles.page}>
      <header style={styles.header}>
        <h2 style={styles.headerTitle}>Gouvernance & Réunions</h2>
        <button onClick={() => navigate('/')} style={{...styles.button, backgroundColor: '#6b7280'}}>← Retour</button>
      </header>

      {hasPermission('manage_rules') && (
        <section>
          <h3 style={styles.sectionTitle}>Règles de la Charte</h3>
          <div style={styles.rulesGrid}>
            {governanceRules.map(category => (
              <div key={category.category} style={styles.card}>
                <h4 style={styles.cardTitle}><FontAwesomeIcon icon={category.icon} style={{marginRight: '10px', color: '#3b82f6'}}/>{category.category}</h4>
                <ul style={styles.rulesList}>
                  {category.rules.map(rule => (
                    <li key={rule.title}><strong>{rule.title}:</strong> {rule.content}</li>
                  ))}
                </ul>
              </div>
            ))}
          </div>
        </section>
      )}

      {hasPermission('manage_meetings') && (
        <section>
          <div style={{...styles.header, marginTop: '40px', marginBottom: '24px'}}>
              <h3 style={styles.sectionTitle}>Calendrier des Réunions</h3>
              <button style={styles.button}><FontAwesomeIcon icon={faPlus} style={{marginRight: '8px'}} /> Planifier une Réunion</button>
          </div>
          <div style={styles.meetingsList}>
              {meetings.map(meeting => (
                  <div key={meeting.id} style={{...styles.card, ...styles.meetingCard}}>
                      <div>
                          <p style={styles.meetingTitle}>{meeting.title}</p>
                          <p style={styles.meetingDate}>
                              <FontAwesomeIcon icon={faCalendarAlt} style={{marginRight: '8px'}}/>
                              {new Date(meeting.date).toLocaleDateString('fr-FR', {weekday: 'long', year: 'numeric', month: 'long', day: 'numeric'})} à {meeting.time}
                          </p>
                      </div>
                      <StatusChip status={meeting.status} />
                  </div>
              ))}
          </div>
        </section>
      )}
    </div>
  );
};

// --- SOUS-COMPOSANTS & STYLES ---
const StatusChip: React.FC<{ status: Meeting['status'] }> = ({ status }) => { const sC = { 'À venir': { backgroundColor: '#dcfce7', color: '#166534' }, 'Passée': { backgroundColor: '#e5e7eb', color: '#4b5563' } }; return <span style={{ ...styles.chip, ...sC[status] }}>{status}</span>; };
const styles: { [key: string]: React.CSSProperties } = {
  page: { fontFamily: 'Arial, sans-serif', padding: '16px', backgroundColor: '#f3f4f6', minHeight: '100vh' },
  header: { display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '24px', flexWrap: 'wrap', gap: '16px' },
  headerTitle: { fontSize: '24px', fontWeight: 'bold', color: '#1f2937', margin: 0 },
  sectionTitle: { fontSize: '22px', fontWeight: 'bold', color: '#1f2937', margin: 0 },
  button: { padding: '10px 16px', backgroundColor: '#2563eb', color: 'white', border: 'none', borderRadius: '8px', cursor: 'pointer', fontWeight: '600', display: 'flex', alignItems: 'center', justifyContent: 'center', fontSize: '14px' },
  rulesGrid: { display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(320px, 1fr))', gap: '24px', marginTop: '24px' },
  card: { backgroundColor: 'white', padding: '24px', borderRadius: '12px', boxShadow: '0 4px 12px rgba(0, 0, 0, 0.08)' },
  cardTitle: { fontSize: '18px', fontWeight: 'bold', margin: '0 0 16px 0', color: '#1f2937', borderBottom: '1px solid #e5e7eb', paddingBottom: '12px', display: 'flex', alignItems: 'center' },
  rulesList: { margin: 0, paddingLeft: '20px', display: 'flex', flexDirection: 'column', gap: '12px', fontSize: '14px', color: '#374151', lineHeight: 1.6 },
  meetingsList: { display: 'flex', flexDirection: 'column', gap: '16px' },
  meetingCard: { display: 'flex', justifyContent: 'space-between', alignItems: 'center', flexWrap: 'wrap', gap: '16px' },
  meetingTitle: { fontWeight: 'bold', margin: 0, color: '#1f2937' },
  meetingDate: { fontSize: '14px', margin: '4px 0 0 0', color: '#4b5563' },
  chip: { padding: '4px 12px', borderRadius: '9999px', fontWeight: '600', fontSize: '12px', flexShrink: 0 },
};

export default Governance;
