import React, { useState, useEffect, ReactNode } from 'react';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';
// Importation des icônes professionnelles
import {
  FaTachometerAlt, FaUsers, FaMoneyBillWave, FaFileInvoiceDollar,
  FaChartBar, FaLandmark, FaCog, FaSignOutAlt, FaBars, FaPlus, FaArrowRight, FaCalendarAlt, FaClock
} from 'react-icons/fa';

// --- COMPOSANT PRINCIPAL: Dashboard ---
const Dashboard: React.FC = () => {
  const { user, logout, hasPermission } = useAuth(); // Supposons que hasPermission existe
  const navigate = useNavigate();
  const [isSidebarOpen, setSidebarOpen] = useState(true);
  const [currentTime, setCurrentTime] = useState(new Date());

  useEffect(() => {
    const timer = setInterval(() => setCurrentTime(new Date()), 1000); // Mise à jour chaque seconde
    return () => clearInterval(timer);
  }, []);

  // Données de simulation
  const berryPoints = 320;
  const fundStatus = {
    totalFund: 150000,
    monthlyContributions: 30000,
    activeMembers: 25,
    loansInRepayment: 5,
    liquidityRate: 'Élevé',
    nextContributionDue: '24-25 du mois',
  };

  const toggleSidebar = () => setSidebarOpen(!isSidebarOpen);

  return (
    <div style={styles.container}>
      {/* --- BARRE LATÉRALE --- */}
      <aside style={{ ...styles.sidebar, width: isSidebarOpen ? '260px' : '0px' }}>
        <div style={styles.sidebarContent}>
          <div style={styles.sidebarHeader}>
            <span style={styles.logo}>🏦</span>
            <h1 style={styles.appName}>Friendly Banks</h1>
            <p style={styles.appSubtitle}>Fonds d'Urgence & Investissement</p>
          </div>
          <nav style={styles.nav}>
            <SidebarButton icon={<FaTachometerAlt />} label="Tableau de bord" onClick={() => navigate('/')} />
            <SidebarButton icon={<FaUsers />} label="Gestion des Membres" onClick={() => navigate('/members')} />
            <SidebarButton icon={<FaMoneyBillWave />} label="Cotisations" onClick={() => navigate('/contributions')} />
            <SidebarButton icon={<FaFileInvoiceDollar />} label="Demandes de Prêts" onClick={() => navigate('/loans')} />
            <SidebarButton icon={<FaChartBar />} label="Statistiques" onClick={() => navigate('/statistics')} />
            <SidebarButton icon={<FaLandmark />} label="Gouvernance" onClick={() => navigate('/governance')} />
            <SidebarButton icon={<FaCog />} label="Paramètres" onClick={() => navigate('/settings')} />
          </nav>
        </div>
        <button onClick={toggleSidebar} style={styles.sidebarToggle}>
          <FaBars />
        </button>
      </aside>

      {/* --- CONTENU PRINCIPAL --- */}
      <main style={{ ...styles.mainContent, paddingLeft: isSidebarOpen ? '284px' : '24px' }}>
        <header style={styles.header}>
          <div>
            <h2 style={styles.headerTitle}>Tableau de bord</h2>
            <p style={styles.headerSubtitle}>Bienvenue, {user?.username || 'Membre'}. Voici votre résumé.</p>
          </div>
          <div style={styles.headerRight}>
            <div style={styles.timeDisplay}>
              <FaCalendarAlt style={{ color: '#6b7280' }} />
              <span>{currentTime.toLocaleDateString('fr-FR', { dateStyle: 'full' })}</span>
              <FaClock style={{ color: '#6b7280', marginLeft: '10px' }}/>
              <span>{currentTime.toLocaleTimeString('fr-FR', { timeStyle: 'short' })}</span>
            </div>
            <button onClick={logout} style={styles.logoutButton}>
              <FaSignOutAlt style={{ marginRight: '8px' }} />
              Déconnexion
            </button>
          </div>
        </header>

        {/* --- CARTES D'ACTION RAPIDE --- */}
        <section style={styles.quickActions}>
          <ActionCard icon={<FaPlus />} title="Nouvelle Cotisation" onClick={() => navigate('/contributions')} />
          <ActionCard icon={<FaFileInvoiceDollar />} title="Demande de Prêt" onClick={() => navigate('/loans')} />
          {hasPermission && hasPermission('add_member') && (
            <ActionCard icon={<FaUsers />} title="Ajouter un Membre" onClick={() => navigate('/members')} />
          )}
        </section>

        {/* --- GRILLE DE DONNÉES PRINCIPALES --- */}
        <section style={styles.dataGrid}>
          {/* Carte des Points Berry */}
          <InfoCard title="Points Berry - Système de Crédit">
            <div style={styles.berryPointsContainer}>
              <div style={styles.berryGauge}>
                <div style={styles.berryGaugeInner}>
                  <span style={styles.berryPointsValue}>{berryPoints}</span>
                  <span style={styles.berryPointsLabel}>points</span>
                </div>
              </div>
              <p style={styles.berryInfo}>Votre pouvoir d’emprunt est calculé sur la base de votre score.</p>
              <a href="/governance#berry-system" style={styles.learnMoreLink}>
                En savoir plus <FaArrowRight style={{ marginLeft: '4px' }} />
              </a>
            </div>
          </InfoCard>

          {/* Carte de l'État du Fonds */}
          <InfoCard title="État du Fonds">
            <ul style={styles.fundStatusList}>
              <FundStatusItem label="Total du fonds collecté" value={`${fundStatus.totalFund.toLocaleString()} XAF`} />
              <FundStatusItem label="Cotisations du mois" value={`${fundStatus.monthlyContributions.toLocaleString()} XAF`} />
              <FundStatusItem label="Membres actifs" value={fundStatus.activeMembers} />
              <FundStatusItem label="Prêts en cours" value={fundStatus.loansInRepayment} />
              <FundStatusItem label="Prochaine échéance" value={fundStatus.nextContributionDue} />
              <FundStatusItem label="Liquidité disponible">
                <StatusChip text={fundStatus.liquidityRate} type={fundStatus.liquidityRate === 'Élevé' ? 'success' : 'warning'} />
              </FundStatusItem>
            </ul>
          </InfoCard>
        </section>
      </main>
    </div>
  );
};

// --- SOUS-COMPOSANTS STYLISÉS ---

const SidebarButton: React.FC<{ icon: ReactNode; label: string; onClick: () => void }> = ({ icon, label, onClick }) => (
  <button onClick={onClick} style={styles.sidebarButton}
    onMouseEnter={e => e.currentTarget.style.backgroundColor = '#2563eb'}
    onMouseLeave={e => e.currentTarget.style.backgroundColor = 'transparent'}>
    <span style={styles.sidebarButtonIcon}>{icon}</span>
    {label}
  </button>
);

const ActionCard: React.FC<{ icon: ReactNode; title: string; onClick: () => void }> = ({ icon, title, onClick }) => (
  <div onClick={onClick} style={styles.actionCard}
    onMouseEnter={e => e.currentTarget.style.backgroundColor = '#1e3a8a'}
    onMouseLeave={e => e.currentTarget.style.backgroundColor = '#2563eb'}>
    <span style={styles.actionCardIcon}>{icon}</span>
    <span style={styles.actionCardTitle}>{title}</span>
  </div>
);

const InfoCard: React.FC<{ title: string; children: ReactNode }> = ({ title, children }) => (
  <div style={styles.infoCard}>
    <h3 style={styles.infoCardTitle}>{title}</h3>
    <div style={styles.infoCardContent}>{children}</div>
  </div>
);

const FundStatusItem: React.FC<{ label: string; value?: string | number; children?: ReactNode }> = ({ label, value, children }) => (
  <li style={styles.fundStatusItem}>
    <span style={styles.fundStatusLabel}>{label}</span>
    {value && <span style={styles.fundStatusValue}>{value}</span>}
    {children}
  </li>
);

const StatusChip: React.FC<{ text: string; type: 'success' | 'warning' | 'danger' }> = ({ text, type }) => {
  const chipColors = {
    success: { backgroundColor: '#dcfce7', color: '#166534' },
    warning: { backgroundColor: '#fef3c7', color: '#92400e' },
    danger: { backgroundColor: '#fee2e2', color: '#991b1b' },
  };
  return <span style={{ ...styles.statusChip, ...chipColors[type] }}>{text}</span>;
};

// --- OBJET DE STYLES CENTRALISÉ ---

const styles: { [key: string]: React.CSSProperties } = {
  container: { display: 'flex', minHeight: '100vh', backgroundColor: '#f3f4f6', fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif" },
  sidebar: { backgroundColor: '#1e3a8a', color: 'white', display: 'flex', flexDirection: 'column', position: 'fixed', top: 0, left: 0, height: '100vh', transition: 'width 0.3s ease', overflow: 'hidden' },
  sidebarContent: { padding: '24px 16px', flexGrow: 1 },
  sidebarHeader: { textAlign: 'center', marginBottom: '32px' },
  logo: { fontSize: '32px' },
  appName: { fontSize: '24px', fontWeight: 'bold', margin: '8px 0 4px 0' },
  appSubtitle: { fontSize: '14px', color: '#93c5fd', margin: 0 },
  nav: { display: 'flex', flexDirection: 'column', gap: '8px' },
  sidebarButton: { background: 'none', border: 'none', color: 'white', fontSize: '16px', fontWeight: 500, textAlign: 'left', padding: '12px 16px', cursor: 'pointer', display: 'flex', alignItems: 'center', gap: '12px', borderRadius: '8px', transition: 'background-color 0.2s' },
  sidebarButtonIcon: { fontSize: '20px' },
  sidebarToggle: { position: 'absolute', top: '24px', right: '-16px', backgroundColor: '#2563eb', border: 'none', borderRadius: '50%', width: '32px', height: '32px', color: 'white', cursor: 'pointer', display: 'flex', alignItems: 'center', justifyContent: 'center', fontSize: '16px', boxShadow: '0 4px 6px rgba(0,0,0,0.1)' },
  mainContent: { flex: 1, padding: '24px', boxSizing: 'border-box', transition: 'padding-left 0.3s ease' },
  header: { display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '32px', flexWrap: 'wrap', gap: '16px' },
  headerTitle: { fontSize: '28px', fontWeight: 'bold', color: '#1f2937', margin: 0 },
  headerSubtitle: { fontSize: '16px', color: '#6b7280', margin: '4px 0 0 0' },
  headerRight: { display: 'flex', alignItems: 'center', gap: '24px', flexWrap: 'wrap' },
  timeDisplay: { display: 'flex', alignItems: 'center', gap: '8px', fontSize: '14px', color: '#4b5563', backgroundColor: '#e5e7eb', padding: '8px 12px', borderRadius: '8px' },
  logoutButton: { padding: '8px 16px', backgroundColor: '#ef4444', color: 'white', border: 'none', borderRadius: '8px', cursor: 'pointer', fontWeight: 600, display: 'flex', alignItems: 'center', transition: 'background-color 0.2s' },
  quickActions: { display: 'flex', gap: '24px', marginBottom: '32px', flexWrap: 'wrap' },
  actionCard: { flex: '1 1 200px', backgroundColor: '#2563eb', color: 'white', borderRadius: '12px', padding: '24px', cursor: 'pointer', display: 'flex', alignItems: 'center', gap: '16px', userSelect: 'none', transition: 'all 0.2s ease', boxShadow: '0 4px 6px rgba(30, 64, 175, 0.2)' },
  actionCardIcon: { fontSize: '28px' },
  actionCardTitle: { fontWeight: 600, fontSize: '18px' },
  dataGrid: { display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(350px, 1fr))', gap: '24px' },
  infoCard: { backgroundColor: 'white', borderRadius: '12px', padding: '24px', boxShadow: '0 4px 12px rgba(0, 0, 0, 0.08)' },
  infoCardTitle: { fontSize: '20px', fontWeight: 'bold', color: '#1f2937', margin: '0 0 16px 0', borderBottom: '1px solid #e5e7eb', paddingBottom: '12px' },
  infoCardContent: { },
  berryPointsContainer: { display: 'flex', flexDirection: 'column', alignItems: 'center', textAlign: 'center' },
  berryGauge: { width: '150px', height: '150px', backgroundColor: '#e5e7eb', borderRadius: '50%', display: 'flex', alignItems: 'center', justifyContent: 'center' },
  berryGaugeInner: { width: '120px', height: '120px', backgroundColor: '#1e3a8a', borderRadius: '50%', display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', color: 'white' },
  berryPointsValue: { fontSize: '40px', fontWeight: 'bold', lineHeight: 1 },
  berryPointsLabel: { fontSize: '14px', color: '#93c5fd' },
  berryInfo: { fontSize: '14px', color: '#4b5563', marginTop: '16px', maxWidth: '300px' },
  learnMoreLink: { color: '#2563eb', textDecoration: 'none', fontWeight: 600, fontSize: '14px', marginTop: '8px', display: 'flex', alignItems: 'center' },
  fundStatusList: { listStyle: 'none', padding: 0, margin: 0, display: 'flex', flexDirection: 'column', gap: '16px' },
  fundStatusItem: { display: 'flex', justifyContent: 'space-between', alignItems: 'center', fontSize: '16px', borderBottom: '1px solid #f3f4f6', paddingBottom: '12px' },
  fundStatusLabel: { color: '#374151', fontWeight: 500 },
  fundStatusValue: { color: '#1f2937', fontWeight: 'bold' },
  statusChip: { padding: '4px 12px', borderRadius: '9999px', fontWeight: 600, fontSize: '12px' },
};

export default Dashboard;
