// src/components/Sanctions.tsx

import React, { useState, useEffect, ChangeEvent, ReactNode, SelectHTMLAttributes, TextareaHTMLAttributes } from 'react';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faGavel, faCheck, faTimes } from '@fortawesome/free-solid-svg-icons';

// --- HOOK POUR LA RESPONSIVITÉ ---
const useWindowSize = () => {
  const [size, setSize] = useState([window.innerWidth]);
  useEffect(() => {
    const handleResize = () => setSize([window.innerWidth]);
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);
  return { width: size[0] };
};

// --- INTERFACES & DONNÉES ---
interface Sanction { id: string; memberId: string; memberName: string; type: 'Avertissement' | 'Amende' | 'Exclusion'; reason: string; date: string; status: 'Vote en cours' | 'Appliquée' | 'Rejetée'; votesFor: number; votesAgainst: number; }
interface Member { id: string; name: string; }
const initialMembers: Member[] = [ { id: 'M001', name: 'Alice Dupont' }, { id: 'M002', name: 'Bob Martin' }, { id: 'M003', name: 'Charlie Brown' }, { id: 'M004', name: 'Diana Prince' } ];
const mockSanctions: Sanction[] = [
  { id: 'S001', memberId: 'M002', memberName: 'Bob Martin', type: 'Amende', reason: "Retard de 3 jours sur la contribution de Juin 2025.", date: '2025-06-28', status: 'Appliquée', votesFor: 15, votesAgainst: 2 },
  { id: 'S002', memberId: 'M004', memberName: 'Diana Prince', type: 'Avertissement', reason: "Absence non justifiée à la réunion de clôture.", date: '2025-07-01', status: 'Vote en cours', votesFor: 5, votesAgainst: 3 },
  { id: 'S003', memberId: 'M001', memberName: 'Alice Dupont', type: 'Exclusion', reason: "Proposition suite à un manquement grave aux engagements financiers.", date: '2025-07-05', status: 'Rejetée', votesFor: 4, votesAgainst: 18 },
];

// --- COMPOSANT PRINCIPAL ---
const Sanctions: React.FC = () => {
  const { user, hasPermission } = useAuth();
  const navigate = useNavigate();
  const { width } = useWindowSize();
  const isMobile = width < 768;

  const [sanctions, setSanctions] = useState<Sanction[]>(mockSanctions);
  const [members] = useState<Member[]>(initialMembers);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [formError, setFormError] = useState('');
  const [votedSanctions, setVotedSanctions] = useState<string[]>([]);
  
  const initialFormState = { memberId: '', type: 'Avertissement' as Sanction['type'], reason: '' };
  const [newSanction, setNewSanction] = useState(initialFormState);

  const handleVote = (id: string, vote: 'for' | 'against') => {
    if (votedSanctions.includes(id)) { alert("Vous avez déjà voté pour cette sanction."); return; }
    setSanctions(sanctions.map(s => s.id === id ? (vote === 'for' ? { ...s, votesFor: s.votesFor + 1 } : { ...s, votesAgainst: s.votesAgainst + 1 }) : s));
    setVotedSanctions([...votedSanctions, id]);
  };

  const handleSaveSanction = () => {
    if (!newSanction.memberId || !newSanction.reason) { setFormError("Veuillez sélectionner un membre et fournir une justification."); return; }
    const member = members.find(m => m.id === newSanction.memberId);
    if (!member) return;
    const sanctionToAdd: Sanction = { id: `S${Date.now()}`, memberId: member.id, memberName: member.name, type: newSanction.type, reason: newSanction.reason, date: new Date().toISOString().split('T')[0], status: 'Vote en cours', votesFor: 1, votesAgainst: 0, };
    setSanctions([sanctionToAdd, ...sanctions]);
    setIsModalOpen(false);
  };
  
  const handleOpenModal = () => {
      setNewSanction(initialFormState);
      setFormError('');
      setIsModalOpen(true);
  };

  if (!hasPermission('manage_rules')) {
    return <div style={styles.page}><p>Vous n'avez pas la permission de voir cette page.</p></div>;
  }

  return (
    <div style={styles.page}>
      <header style={styles.header}><h2 style={styles.headerTitle}>Sanctions & Exclusions</h2><button onClick={() => navigate('/')} style={{...styles.button, backgroundColor: '#6b7280'}}>← Retour</button></header>
      <section style={styles.controlsSection}><button onClick={handleOpenModal} style={styles.button}><FontAwesomeIcon icon={faGavel} style={{ marginRight: '8px' }} /> Proposer une Sanction</button></section>

      {isMobile ? 
        <MobileSanctionList sanctions={sanctions} onVote={handleVote} votedSanctions={votedSanctions} /> : 
        <DesktopSanctionTable sanctions={sanctions} onVote={handleVote} votedSanctions={votedSanctions} />
      }

      {isModalOpen && (
          <div style={styles.modalOverlay}>
              <div style={styles.modalContent}>
                  <h3 style={styles.modalTitle}>Proposer une Sanction</h3>
                  {formError && <p style={styles.formError}>{formError}</p>}
                  <div style={styles.modalForm}>
                      <ModalSelect label="Membre concerné" value={newSanction.memberId} onChange={(e: ChangeEvent<HTMLSelectElement>) => setNewSanction({...newSanction, memberId: e.target.value})}>
                          <option value="" disabled>Sélectionner un membre...</option>
                          {members.map(m => <option key={m.id} value={m.id}>{m.name}</option>)}
                      </ModalSelect>
                      <ModalSelect label="Type de sanction" value={newSanction.type} onChange={(e: ChangeEvent<HTMLSelectElement>) => setNewSanction({...newSanction, type: e.target.value as Sanction['type']})}>
                          <option value="Avertissement">Avertissement</option><option value="Amende">Amende</option><option value="Exclusion">Exclusion</option>
                      </ModalSelect>
                      <ModalTextarea label="Justification / Motif" value={newSanction.reason} onChange={(e: ChangeEvent<HTMLTextAreaElement>) => setNewSanction({...newSanction, reason: e.target.value})} placeholder="Décrivez clairement le manquement..."/>
                  </div>
                  <div style={styles.modalActions}>
                      <button onClick={() => setIsModalOpen(false)} style={{...styles.button, backgroundColor: '#6b7280'}}>Annuler</button>
                      <button onClick={handleSaveSanction} style={styles.button}>Soumettre au Vote</button>
                  </div>
              </div>
          </div>
      )}
    </div>
  );
};

// --- VUES DESKTOP & MOBILE ---
const DesktopSanctionTable: React.FC<{sanctions: Sanction[], onVote: Function, votedSanctions: string[]}> = ({ sanctions, onVote, votedSanctions }) => (
    <div style={styles.tableContainer}>
        <table style={styles.table}>
            <thead><tr><th style={styles.th}>Membre</th><th style={styles.th}>Type</th><th style={styles.th}>Motif</th><th style={styles.th}>Date</th><th style={styles.th}>Statut & Votes</th><th style={styles.th}>Actions</th></tr></thead>
            <tbody>
                {sanctions.map(s => (
                    <tr key={s.id}>
                        <td style={styles.td}>{s.memberName}</td><td style={styles.td}><TypeChip type={s.type} /></td>
                        <td style={styles.td}><p style={styles.reasonText}>{s.reason}</p></td>
                        <td style={styles.td}>{new Date(s.date).toLocaleDateString('fr-FR')}</td>
                        <td style={styles.td}>
                            <div style={styles.voteInfo}><StatusChip status={s.status}/>
                                <span style={{fontSize: '12px', color: '#4b5563'}}>Pour: <strong style={{color: '#166534'}}>{s.votesFor}</strong> / Contre: <strong style={{color: '#991b1b'}}>{s.votesAgainst}</strong></span>
                            </div>
                        </td>
                        <td style={styles.td}>{s.status === 'Vote en cours' && (
                                <div style={styles.actionGroup}>
                                    <button onClick={() => onVote(s.id, 'for')} disabled={votedSanctions.includes(s.id)} style={{...styles.actionButton, backgroundColor: '#22c55e'}} title="Voter Pour"><FontAwesomeIcon icon={faCheck}/></button>
                                    <button onClick={() => onVote(s.id, 'against')} disabled={votedSanctions.includes(s.id)} style={{...styles.actionButton, backgroundColor: '#ef4444'}} title="Voter Contre"><FontAwesomeIcon icon={faTimes}/></button>
                                </div>)}
                        </td>
                    </tr>
                ))}
            </tbody>
        </table>
    </div>
);
const MobileSanctionList: React.FC<{sanctions: Sanction[], onVote: Function, votedSanctions: string[]}> = ({ sanctions, onVote, votedSanctions }) => (
    <div style={styles.mobileList}>
        {sanctions.map(s => (
            <div key={s.id} style={styles.mobileCard}>
                <div style={styles.mobileCardHeader}>
                    <div><span style={styles.mobileCardTitle}>{s.memberName}</span><TypeChip type={s.type} /></div><StatusChip status={s.status}/>
                </div>
                <div style={styles.mobileCardBody}>
                    <p style={styles.reasonTextMobile}>{s.reason}</p>
                    <div style={styles.mobileCardRow}><span>Votes:</span>
                        <span style={{fontSize: '14px', color: '#4b5563'}}>Pour: <strong style={{color: '#166534'}}>{s.votesFor}</strong> / Contre: <strong style={{color: '#991b1b'}}>{s.votesAgainst}</strong></span>
                    </div>
                </div>
                {s.status === 'Vote en cours' && (
                    <div style={styles.mobileCardFooter}>
                        <button disabled={votedSanctions.includes(s.id)} onClick={() => onVote(s.id, 'for')} style={{...styles.button, flex: 1, backgroundColor: '#22c55e'}}><FontAwesomeIcon icon={faCheck} style={{marginRight: '8px'}}/> Pour</button>
                        <button disabled={votedSanctions.includes(s.id)} onClick={() => onVote(s.id, 'against')} style={{...styles.button, flex: 1, backgroundColor: '#ef4444'}}><FontAwesomeIcon icon={faTimes} style={{marginRight: '8px'}}/> Contre</button>
                    </div>
                )}
            </div>
        ))}
    </div>
);

// --- SOUS-COMPOSANTS & STYLES ---
const StatusChip: React.FC<{ status: Sanction['status'] }> = ({ status }) => { const sC = { 'Vote en cours': { backgroundColor: '#fef3c7', color: '#92400e' }, 'Appliquée': { backgroundColor: '#dcfce7', color: '#166534' }, 'Rejetée': { backgroundColor: '#fee2e2', color: '#991b1b' } }; return <span style={{ ...styles.chip, ...sC[status] }}>{status}</span>; };
const TypeChip: React.FC<{ type: Sanction['type'] }> = ({ type }) => { const tC = { 'Avertissement': { backgroundColor: '#fef9c3', color: '#854d0e' }, 'Amende': { backgroundColor: '#fed7aa', color: '#9a3412' }, 'Exclusion': { backgroundColor: '#fecaca', color: '#991b1b' } }; return <span style={{ ...styles.chip, ...tC[type] }}>{type}</span>; };
interface ModalSelectProps extends SelectHTMLAttributes<HTMLSelectElement> { label: string; children: ReactNode; }
const ModalSelect: React.FC<ModalSelectProps> = ({ label, children, ...props }) => (<div><label style={styles.modalLabel}>{label}</label><select style={styles.modalInput} {...props}>{children}</select></div>);
interface ModalTextareaProps extends TextareaHTMLAttributes<HTMLTextAreaElement> { label: string; }
const ModalTextarea: React.FC<ModalTextareaProps> = ({ label, ...props }) => (<div><label style={styles.modalLabel}>{label}</label><textarea style={{...styles.modalInput, height: '100px'}} {...props} /></div>);

const styles: { [key: string]: React.CSSProperties } = {
  page: { fontFamily: 'Arial, sans-serif', padding: '16px', backgroundColor: '#f3f4f6', minHeight: '100vh' },
  header: { display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '24px', flexWrap: 'wrap', gap: '16px' },
  headerTitle: { fontSize: '24px', fontWeight: 'bold', color: '#1f2937', margin: 0 },
  controlsSection: { marginBottom: '24px' },
  button: { padding: '10px 16px', backgroundColor: '#2563eb', color: 'white', border: 'none', borderRadius: '8px', cursor: 'pointer', fontWeight: '600', display: 'flex', alignItems: 'center', justifyContent: 'center', transition: 'background-color 0.2s', fontSize: '14px' },
  tableContainer: { backgroundColor: 'white', borderRadius: '12px', boxShadow: '0 4px 12px rgba(0, 0, 0, 0.08)', overflowX: 'auto' },
  table: { width: '100%', borderCollapse: 'collapse' },
  th: { padding: '12px 16px', textAlign: 'left', fontSize: '14px', color: '#4b5563', backgroundColor: '#f9fafb', borderBottom: '1px solid #e5e7eb', fontWeight: '600' },
  td: { padding: '12px 16px', fontSize: '14px', color: '#374151', borderBottom: '1px solid #e5e7eb', verticalAlign: 'middle' },
  chip: { padding: '4px 12px', borderRadius: '9999px', fontWeight: '600', fontSize: '12px', marginBottom: '4px', display: 'inline-block' },
  reasonText: { maxWidth: '300px', whiteSpace: 'normal', margin: 0 },
  reasonTextMobile: { fontSize: '14px', margin: '0 0 12px 0', color: '#4b5563' },
  voteInfo: { display: 'flex', flexDirection: 'column', gap: '4px' },
  actionGroup: { display: 'flex', gap: '8px' },
  actionButton: { width: '32px', height: '32px', border: 'none', borderRadius: '50%', color: 'white', cursor: 'pointer', display: 'flex', alignItems: 'center', justifyContent: 'center', fontSize: '14px', opacity: 1, transition: 'opacity 0.2s' },
  modalOverlay: { position: 'fixed', top: 0, left: 0, right: 0, bottom: 0, backgroundColor: 'rgba(0, 0, 0, 0.6)', display: 'flex', justifyContent: 'center', alignItems: 'center', zIndex: 1000, padding: '16px' },
  modalContent: { backgroundColor: 'white', padding: '24px', borderRadius: '12px', width: '100%', maxWidth: '500px', boxShadow: '0 8px 16px rgba(0, 0, 0, 0.2)', maxHeight: '90vh', display: 'flex', flexDirection: 'column' },
  modalTitle: { fontSize: '22px', fontWeight: 'bold', marginBottom: '24px', color: '#1f2937', flexShrink: 0, textAlign: 'center' },
  modalForm: { flexGrow: 1, overflowY: 'auto', display: 'flex', flexDirection: 'column', gap: '16px' },
  modalLabel: { display: 'block', marginBottom: '8px', fontWeight: '600', color: '#374151', fontSize: '14px' },
  modalInput: { width: '100%', padding: '10px', border: '1px solid #d1d5db', borderRadius: '8px', boxSizing: 'border-box', fontSize: '16px' },
  modalActions: { display: 'flex', justifyContent: 'flex-end', gap: '12px', marginTop: '24px', flexShrink: 0 },
  formError: { color: '#ef4444', backgroundColor: '#fee2e2', padding: '10px', borderRadius: '8px', textAlign: 'center', marginBottom: '16px' },
  mobileList: { display: 'flex', flexDirection: 'column', gap: '16px' },
  mobileCard: { backgroundColor: 'white', borderRadius: '12px', boxShadow: '0 4px 12px rgba(0, 0, 0, 0.08)', overflow: 'hidden'},
  mobileCardHeader: { display: 'flex', justifyContent: 'space-between', alignItems: 'center', padding: '12px 16px', backgroundColor: '#f9fafb', borderBottom: '1px solid #e5e7eb'},
  mobileCardTitle: { fontWeight: 'bold', color: '#1f2937' },
  mobileCardBody: { padding: '16px' },
  mobileCardRow: { display: 'flex', justifyContent: 'space-between', fontSize: '14px' },
  mobileCardFooter: { padding: '12px 16px', backgroundColor: '#f9fafb', borderTop: '1px solid #e5e7eb', display: 'flex', gap: '12px' },
};

export default Sanctions;
