import React, { useState, useEffect, useMemo, ReactNode, InputHTMLAttributes, SelectHTMLAttributes, TextareaHTMLAttributes, ChangeEvent } from 'react';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';
import { FaPlus, FaCheck, FaTimes } from 'react-icons/fa';

// --- INTERFACES & CONSTANTES ---

interface Loan {
  id: string;
  memberId: string;
  memberName: string;
  amount: number;
  interestRate: number; // en %
  totalToRepay: number;
  date: string;
  status: 'En attente' | 'Approuvé' | 'Rejeté' | 'Remboursé';
  justification: string;
  guarantors: { id: string, name: string }[];
}

interface Member {
  id: string;
  name: string;
  berryPoints: number;
}

const MOCK_TOTAL_FUND = 150000;

// --- DONNÉES DE SIMULATION ---

const initialMembers: Member[] = [
  { id: 'M001', name: 'Alice Dupont', berryPoints: 120 },
  { id: 'M002', name: 'Bob Martin', berryPoints: 85 },
  { id: 'M003', name: 'Charlie Brown', berryPoints: 250 },
  { id: 'M004', name: 'Diana Prince', berryPoints: 45 },
];

const mockLoans: Loan[] = [
    { id: 'L001', memberId: 'M002', memberName: 'Bob Martin', amount: 50000, interestRate: 8, totalToRepay: 54000, date: '2025-06-20', status: 'Approuvé', justification: 'Frais médicaux urgents', guarantors: [{id: 'M001', name: 'Alice Dupont'}, {id: 'M003', name: 'Charlie Brown'}]},
    { id: 'L002', memberId: 'M004', memberName: 'Diana Prince', amount: 30000, interestRate: 10, totalToRepay: 33000, date: '2025-07-01', status: 'En attente', justification: 'Réparation voiture suite à un accident', guarantors: [{id: 'M001', name: 'Alice Dupont'}, {id: 'M002', name: 'Bob Martin'}]},
    { id: 'L003', memberId: 'M001', memberName: 'Alice Dupont', amount: 150000, interestRate: 6, totalToRepay: 159000, date: '2025-05-10', status: 'Remboursé', justification: 'Acompte projet immobilier', guarantors: [{id: 'M002', name: 'Bob Martin'}, {id: 'M003', name: 'Charlie Brown'}]},
];

// --- SERVICE DE LOGIQUE MÉTIER POUR LES PRÊTS ---
const LoanService = {
  getMaxLoanAmount: (berryPoints: number): number => {
    if (berryPoints >= 10 && berryPoints <= 100) return 60000;
    if (berryPoints > 100 && berryPoints <= 199) return 120000;
    if (berryPoints >= 200 && berryPoints <= 499) return 300000;
    if (berryPoints >= 500) return MOCK_TOTAL_FUND * 0.7;
    return 0;
  },
  getInterestRate: (amount: number): number => {
    if (amount > 0 && amount < 40000) return 10;
    if (amount >= 40000 && amount < 100000) return 8;
    if (amount >= 100000) return 6;
    return 0;
  }
};

// --- COMPOSANT PRINCIPAL ---
const Loans: React.FC = () => {
  const { user, hasPermission } = useAuth();
  const navigate = useNavigate();

  const [loans, setLoans] = useState<Loan[]>(mockLoans);
  const [members] = useState<Member[]>(initialMembers);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [formError, setFormError] = useState('');
  
  // CORRECTION: Utilisation de user.id
  const initialFormState = {
    memberId: user?.id || '',
    amount: 5000,
    justification: '',
    guarantor1Id: '',
    guarantor2Id: '',
  };
  const [newRequest, setNewRequest] = useState(initialFormState);

  const selectedMember = useMemo(() => members.find(m => m.id === newRequest.memberId), [newRequest.memberId, members]);
  
  const loanDetails = useMemo(() => {
    if (!selectedMember) return null;
    const maxAmount = LoanService.getMaxLoanAmount(selectedMember.berryPoints);
    const interestRate = LoanService.getInterestRate(newRequest.amount);
    const totalToRepay = newRequest.amount * (1 + interestRate / 100);
    let error = '';
    if (newRequest.amount > maxAmount) error = `Le montant dépasse votre plafond de prêt de ${maxAmount.toLocaleString()} XAF.`;
    else if (newRequest.amount > MOCK_TOTAL_FUND * 0.7) error = `Le montant dépasse 70% des fonds disponibles.`;
    return { maxAmount, interestRate, totalToRepay, error };
  }, [newRequest.amount, selectedMember]);

  const handleOpenModal = () => {
    setNewRequest(initialFormState);
    setFormError('');
    setIsModalOpen(true);
  };

  const handleStatusUpdate = (id: string, status: 'Approuvé' | 'Rejeté') => {
    if (!hasPermission('approve_loans')) {
      alert("Action non autorisée.");
      return;
    }
    setLoans(loans.map(loan => loan.id === id ? { ...loan, status } : loan));
  };

  const handleSaveRequest = () => {
    if (!selectedMember || !loanDetails || !newRequest.justification || !newRequest.guarantor1Id || !newRequest.guarantor2Id) {
      setFormError("Veuillez remplir tous les champs.");
      return;
    }
    if (loanDetails.error) {
      setFormError(loanDetails.error);
      return;
    }
    if (newRequest.guarantor1Id === newRequest.guarantor2Id || newRequest.guarantor1Id === selectedMember.id || newRequest.guarantor2Id === selectedMember.id) {
        setFormError("Veuillez choisir deux avaliseurs distincts et différents de vous-même.");
        return;
    }
    const guarantor1 = members.find(m => m.id === newRequest.guarantor1Id);
    const guarantor2 = members.find(m => m.id === newRequest.guarantor2Id);
    if(!guarantor1 || !guarantor2) {
        setFormError("Avaliseurs invalides.");
        return;
    }
    const newLoan: Loan = {
      id: `L${Date.now()}`,
      memberId: selectedMember.id,
      memberName: selectedMember.name,
      amount: newRequest.amount,
      interestRate: loanDetails.interestRate,
      totalToRepay: loanDetails.totalToRepay,
      date: new Date().toISOString().split('T')[0],
      status: 'En attente',
      justification: newRequest.justification,
      guarantors: [{id: guarantor1.id, name: guarantor1.name}, {id: guarantor2.id, name: guarantor2.name}],
    };
    setLoans([newLoan, ...loans]);
    setIsModalOpen(false);
  };

  const loanStats = useMemo(() => ({
    pendingCount: loans.filter(l => l.status === 'En attente').length,
    activeLoanAmount: loans.filter(l => l.status === 'Approuvé').reduce((sum, l) => sum + l.amount, 0),
    repaidAmount: loans.filter(l => l.status === 'Remboursé').reduce((sum, l) => sum + l.amount, 0),
  }), [loans]);
  
  return (
    <div style={styles.page}>
      <header style={styles.header}>
        <h2 style={styles.headerTitle}>Gestion des Prêts</h2>
        <button onClick={() => navigate('/')} style={{...styles.button, backgroundColor: '#6b7280'}}>← Retour</button>
      </header>
      
      <section style={styles.statSection}>
        <StatCard title="Total des Prêts Actifs" value={`${loanStats.activeLoanAmount.toLocaleString()} XAF`} />
        <StatCard title="Demandes en Attente" value={loanStats.pendingCount} />
        <StatCard title="Total Remboursé" value={`${loanStats.repaidAmount.toLocaleString()} XAF`} />
      </section>

      <section style={styles.controlsSection}>
        <button onClick={handleOpenModal} style={styles.button}>
          <FaPlus style={{ marginRight: '8px' }} />
          Faire une Demande de Prêt
        </button>
      </section>

      <div style={styles.tableContainer}>
        <table style={styles.table}>
          <thead>
            <tr>
              <th style={styles.th}>Membre</th>
              <th style={styles.th}>Montant</th>
              <th style={styles.th}>Taux</th>
              <th style={styles.th}>Total</th>
              <th style={styles.th}>Date</th>
              <th style={styles.th}>Statut</th>
              <th style={styles.th}>Actions</th>
            </tr>
          </thead>
          <tbody>
            {loans.map(loan => (
              <tr key={loan.id}>
                <td style={styles.td}>{loan.memberName}</td>
                <td style={styles.td}>{loan.amount.toLocaleString()} XAF</td>
                <td style={styles.td}>{loan.interestRate}%</td>
                <td style={styles.td}>{loan.totalToRepay.toLocaleString()} XAF</td>
                <td style={styles.td}>{new Date(loan.date).toLocaleDateString('fr-FR')}</td>
                <td style={styles.td}><StatusChip status={loan.status} /></td>
                <td style={styles.td}>
                  {loan.status === 'En attente' && hasPermission('approve_loans') && (
                    <div style={{ display: 'flex', gap: '8px' }}>
                      <button onClick={() => handleStatusUpdate(loan.id, 'Approuvé')} style={{...styles.actionButton, backgroundColor: '#22c55e'}}><FaCheck /></button>
                      <button onClick={() => handleStatusUpdate(loan.id, 'Rejeté')} style={{...styles.actionButton, backgroundColor: '#ef4444'}}><FaTimes /></button>
                    </div>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {isModalOpen && (
        <div style={styles.modalOverlay}>
          <div style={styles.modalContent}>
            <h3 style={styles.modalTitle}>Nouvelle Demande de Prêt</h3>
            {formError && <p style={styles.formError}>{formError}</p>}
            
            <div style={styles.modalForm}>
                <ModalSelect label="Membre demandeur" value={newRequest.memberId} onChange={e => setNewRequest({...newRequest, memberId: e.target.value})}>
                    <option value="" disabled>Sélectionner un membre</option>
                    {members.map(m => <option key={m.id} value={m.id}>{m.name} ({m.berryPoints} pts)</option>)}
                </ModalSelect>
                {selectedMember && (
                    <>
                        <ModalInput label="Montant souhaité (XAF)" type="number" step="1000" value={newRequest.amount} onChange={e => setNewRequest({...newRequest, amount: parseFloat(e.target.value) || 0})} />
                        {loanDetails && (
                            <div style={styles.loanDetailsBox}>
                                <p>Plafond de prêt: <strong>{loanDetails.maxAmount.toLocaleString()} XAF</strong></p>
                                <p>Taux d'intérêt: <strong>{loanDetails.interestRate}%</strong></p>
                                <p>Total à rembourser: <strong>{loanDetails.totalToRepay.toLocaleString()} XAF</strong></p>
                                {loanDetails.error && <p style={{color: '#ef4444'}}>{loanDetails.error}</p>}
                            </div>
                        )}
                        <ModalTextarea label="Motif de la demande (urgence avérée)" value={newRequest.justification} onChange={e => setNewRequest({...newRequest, justification: e.target.value})} />
                        <ModalSelect label="Avaliseur / Garant 1" value={newRequest.guarantor1Id} onChange={e => setNewRequest({...newRequest, guarantor1Id: e.target.value})}>
                            <option value="" disabled>Sélectionner...</option>
                            {members.filter(m => m.id !== newRequest.memberId).map(m => <option key={m.id} value={m.id}>{m.name}</option>)}
                        </ModalSelect>
                        <ModalSelect label="Avaliseur / Garant 2" value={newRequest.guarantor2Id} onChange={e => setNewRequest({...newRequest, guarantor2Id: e.target.value})}>
                            <option value="" disabled>Sélectionner...</option>
                            {members.filter(m => m.id !== newRequest.memberId).map(m => <option key={m.id} value={m.id}>{m.name}</option>)}
                        </ModalSelect>
                    </>
                )}
            </div>
            <div style={styles.modalActions}>
              <button onClick={() => setIsModalOpen(false)} style={{...styles.button, backgroundColor: '#6b7280'}}>Annuler</button>
              <button onClick={handleSaveRequest} style={styles.button}>Soumettre la Demande</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

// --- SOUS-COMPOSANTS STYLISÉS ET D'ASSISTANCE ---
const StatCard: React.FC<{ title: string; value: string | number }> = ({ title, value }) => (<div style={{...styles.card, flex: '1 1 250px', textAlign: 'center' }}><h4 style={styles.statTitle}>{title}</h4><p style={styles.statValue}>{value}</p></div>);
const StatusChip: React.FC<{ status: Loan['status'] }> = ({ status }) => {
    const statusColors = { 'En attente': { backgroundColor: '#fef3c7', color: '#92400e' }, 'Approuvé': { backgroundColor: '#dcfce7', color: '#166534' }, 'Rejeté': { backgroundColor: '#fee2e2', color: '#991b1b' }, 'Remboursé': { backgroundColor: '#e0e7ff', color: '#3730a3' } };
    return <span style={{ ...styles.chip, ...statusColors[status] }}>{status}</span>;
};

// CORRECTION: Typage explicite des props et des événements
interface ModalInputProps extends InputHTMLAttributes<HTMLInputElement> { label: string; }
const ModalInput: React.FC<ModalInputProps> = ({ label, ...props }) => (<div><label style={styles.modalLabel}>{label}</label><input style={styles.modalInput} {...props} /></div>);

interface ModalSelectProps extends SelectHTMLAttributes<HTMLSelectElement> { label: string; children: ReactNode; }
const ModalSelect: React.FC<ModalSelectProps> = ({ label, children, ...props }) => (<div><label style={styles.modalLabel}>{label}</label><select style={styles.modalInput} {...props}>{children}</select></div>);

interface ModalTextareaProps extends TextareaHTMLAttributes<HTMLTextAreaElement> { label: string; }
const ModalTextarea: React.FC<ModalTextareaProps> = ({ label, ...props }) => (<div><label style={styles.modalLabel}>{label}</label><textarea style={{...styles.modalInput, height: '80px'}} {...props} /></div>);


// --- OBJET DE STYLES CENTRALISÉ ---
const styles: { [key: string]: React.CSSProperties } = {
  page: { fontFamily: 'Arial, sans-serif', padding: '24px', backgroundColor: '#f3f4f6', minHeight: '100vh' },
  header: { display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '32px', flexWrap: 'wrap', gap: '16px' },
  headerTitle: { fontSize: '28px', fontWeight: 'bold', color: '#1f2937', margin: 0 },
  statSection: { display: 'flex', gap: '24px', flexWrap: 'wrap', marginBottom: '32px' },
  card: { backgroundColor: 'white', padding: '20px', borderRadius: '12px', boxShadow: '0 4px 12px rgba(0, 0, 0, 0.08)' },
  statTitle: { margin: 0, color: '#4b5563', fontSize: '16px', fontWeight: '600' },
  statValue: { margin: '8px 0 0 0', color: '#1e3a8a', fontSize: '28px', fontWeight: 'bold' },
  controlsSection: { marginBottom: '24px' },
  button: { padding: '10px 20px', backgroundColor: '#2563eb', color: 'white', border: 'none', borderRadius: '8px', cursor: 'pointer', fontWeight: '600', display: 'flex', alignItems: 'center', justifyContent: 'center' },
  tableContainer: { backgroundColor: 'white', borderRadius: '12px', boxShadow: '0 4px 12px rgba(0, 0, 0, 0.08)', overflowX: 'auto' },
  table: { width: '100%', borderCollapse: 'collapse' },
  th: { padding: '12px 16px', textAlign: 'left', fontSize: '14px', color: '#4b5563', backgroundColor: '#f9fafb', borderBottom: '1px solid #e5e7eb', fontWeight: '600' },
  td: { padding: '12px 16px', fontSize: '14px', color: '#374151', borderBottom: '1px solid #e5e7eb' },
  chip: { padding: '4px 12px', borderRadius: '9999px', fontWeight: '600', fontSize: '12px' },
  actionButton: { width: '32px', height: '32px', border: 'none', borderRadius: '50%', color: 'white', cursor: 'pointer', display: 'flex', alignItems: 'center', justifyContent: 'center' },
  modalOverlay: { position: 'fixed', top: 0, left: 0, right: 0, bottom: 0, backgroundColor: 'rgba(0, 0, 0, 0.5)', display: 'flex', justifyContent: 'center', alignItems: 'center', zIndex: 1000 },
  modalContent: { backgroundColor: 'white', padding: '32px', borderRadius: '12px', width: '90%', maxWidth: '600px', boxShadow: '0 8px 16px rgba(0, 0, 0, 0.2)' },
  modalTitle: { fontSize: '24px', fontWeight: 'bold', marginBottom: '24px', color: '#1f2937' },
  modalForm: { display: 'flex', flexDirection: 'column', gap: '16px' },
  modalLabel: { display: 'block', marginBottom: '8px', fontWeight: '600', color: '#374151', fontSize: '14px' },
  modalInput: { width: '100%', padding: '10px', border: '1px solid #d1d5db', borderRadius: '8px', boxSizing: 'border-box', fontSize: '16px' },
  modalActions: { display: 'flex', justifyContent: 'flex-end', gap: '12px', marginTop: '32px' },
  formError: { color: '#ef4444', backgroundColor: '#fee2e2', padding: '10px', borderRadius: '8px', textAlign: 'center', marginBottom: '16px' },
  loanDetailsBox: { backgroundColor: '#f3f4f6', padding: '12px', borderRadius: '8px', fontSize: '14px', lineHeight: 1.6, borderLeft: '4px solid #3b82f6'},
};

export default Loans;
