import React, { useState, useEffect, useMemo } from 'react';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';

interface Address {
  street: string;
  city: string;
  postalCode: string;
  country: string;
}
interface EmergencyContact {
  name: string;
  phone: string;
  relation: string;
}
interface PointsHistory {
  date: string;
  points: number;
  reason: string;
  type: 'gain' | 'loss';
}
interface Contribution {
  date: string;
  amount: number;
  status: 'paid' | 'pending' | 'overdue';
  penalties?: number;
  berryPointsEarned?: number;
}
interface Loan {
  loanId: string;
  amount: number;
  status: 'active' | 'completed' | 'defaulted';
  startDate: string;
  endDate?: string;
  monthlyPayment: number;
}
interface Sanction {
  id: string;
  date: string;
  type: 'penalty' | 'suspension' | 'exclusion';
  reason: string;
  amount?: number;
  status: 'active' | 'resolved';
}
type MemberRole = 'President' | 'Treasurer' | 'Secrecom' | 'Accountant' | 'Censor' | 'Member' | 'Guest';
type MemberStatus = 'Actif' | 'Suspendu' | 'Exclu';
type EligibilityLevel = 'Bronze' | 'Silver' | 'Gold' | 'Platinum';
interface Member {
  id: string;
  personalInfo: {
    firstName: string;
    lastName: string;
    email: string;
    phone: string;
    address: Address;
    dateOfBirth: string;
    nationality: string;
    profession: string;
    emergencyContact: EmergencyContact;
    profilePhoto?: string;
  };
  accountInfo: {
    role: MemberRole;
    joinDate: string;
    membershipNumber: string;
    status: MemberStatus;
    lastLoginDate: string;
  };
  berryPoints: {
    totalPoints: number;
    pointsHistory: PointsHistory[];
    lastUpdate: string;
    eligibilityLevel: EligibilityLevel;
    monthlyContributions: number;
    attendanceRate: number;
  };
  financial: {
    totalContributions: number;
    currentBalance: number;
    loanEligibility: number;
    activeLoans: Loan[];
    contributionHistory: Contribution[];
    sanctions: Sanction[];
    monthlyPenalties: number;
  };
  contributionStatus: 'à jour' | 'en retard' | 'absent';
  loanHistory?: string[];
  isActive?: boolean;
}
const roleLimits: { [key in MemberRole]: number } = {
  President: 1,
  Treasurer: 2,
  Secrecom: 1,
  Accountant: 2,
  Censor: 1,
  Member: Infinity,
  Guest: Infinity,
};
// Fonction de calcul des points Berry selon la formule
const calculateBerryPoints = (
  monthlyContributions: number,
  attendanceRate: number,
  monthsActive: number
): number => {
  const S = 500; // Base points
  const C = monthlyContributions / 1000; // Contributions en milliers
  const τ = attendanceRate / 100; // Taux de présence en décimal
  const ε = monthsActive; // Nombre de mois d'activité

  const formula = S * (1 + C / 10) * (τ / (ε + 1)) * 0.85 * τ;
  return Math.round(formula);
};

// Fonction de calcul du pouvoir d'emprunt
const calculateLoanEligibility = (berryPoints: number): number => {
  if (berryPoints >= 500) return 500000; // Selon fonds disponibles (max 500k)
  if (berryPoints >= 200) return 300000;
  if (berryPoints >= 100) return 120000;
  if (berryPoints >= 10) return 60000;
  return 0;
};

// Fonction de calcul des pénalités
const calculatePenalties = (daysOverdue: number): number => {
  return daysOverdue * 200; // 200 XAF par jour de retard
};

// Fonction de détermination du niveau d'éligibilité
const getEligibilityLevel = (berryPoints: number): EligibilityLevel => {
  if (berryPoints >= 500) return 'Platinum';
  if (berryPoints >= 200) return 'Gold';
  if (berryPoints >= 100) return 'Silver';
  return 'Bronze';
};

// Fonction pour mettre à jour les points Berry d'un membre
const updateMemberBerryPoints = (member: Member): Member => {
  const newPoints = calculateBerryPoints(
    member.berryPoints.monthlyContributions,
    member.berryPoints.attendanceRate,
    member.financial.monthlyPenalties
  );
  
  const eligibilityLevel = getEligibilityLevel(newPoints);
  const loanEligibility = calculateLoanEligibility(newPoints);
  
  return {
    ...member,
    berryPoints: {
      ...member.berryPoints,
      totalPoints: newPoints,
      eligibilityLevel,
      lastUpdate: new Date().toISOString(),
    },
    financial: {
      ...member.financial,
      loanEligibility,
    }
  };
};

const Members: React.FC = () => {
  const { user, hasPermission } = useAuth();
  const navigate = useNavigate();
  const [members, setMembers] = useState<Member[]>([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [roleFilter, setRoleFilter] = useState('');
  const [statusFilter, setStatusFilter] = useState('');
  const [isModalOpen, setModalOpen] = useState(false);
  const [editingMember, setEditingMember] = useState<Member | null>(null);
  const [formErrors, setFormErrors] = useState<{ [key: string]: string }>({});

  // New state for country code and local phone number
  const [selectedCountryCode, setSelectedCountryCode] = useState<string>('+237');
  const [localPhoneNumber, setLocalPhoneNumber] = useState<string>('');

  // List of country codes for selection
  const countryCodes = [
    { code: '+1', label: 'USA (+1)' },
    { code: '+44', label: 'UK (+44)' },
    { code: '+237', label: 'Cameroon (+237)' },
    { code: '+33', label: 'France (+33)' },
    { code: '+49', label: 'Germany (+49)' },
    { code: '+91', label: 'India (+91)' },
    { code: '+81', label: 'Japan (+81)' },
    { code: '+86', label: 'China (+86)' },
    { code: '+254', label: 'Kenya (+254)' },
    { code: '+27', label: 'South Africa (+27)' },
  ];

  // Helper to split phone into country code and local number
  const splitPhoneNumber = (phone: string) => {
    if (!phone) return { countryCode: '+237', localNumber: '' };
    
    // Sort country codes by length (longest first) to match correctly
    const sortedCodes = [...countryCodes].sort((a, b) => b.code.length - a.code.length);
    
    for (const c of sortedCodes) {
      if (phone.startsWith(c.code)) {
        return { countryCode: c.code, localNumber: phone.slice(c.code.length) };
      }
    }
    // Default fallback
    return { countryCode: '+237', localNumber: phone };
  };

  // When editingMember changes, update country code and local phone number states
  useEffect(() => {
    if (editingMember) {
      const { countryCode, localNumber } = splitPhoneNumber(editingMember.personalInfo.phone);
      setSelectedCountryCode(countryCode);
      setLocalPhoneNumber(localNumber);
    } else {
      setSelectedCountryCode('+237');
      setLocalPhoneNumber('');
    }
  }, [editingMember]);

  useEffect(() => {
    const fetchMembers = async () => {
      try {
        // TODO: Replace with actual API call
        // const response = await fetch('/api/members', { headers: { Authorization: `Bearer ${user?.token}` } });
        // const data = await response.json();
        // setMembers(data);

        setMembers([
          { id: '1', personalInfo: { firstName: 'John', lastName: 'Doe', email: 'john.doe@example.com', phone: '+237699887766', address: { street: '123 Main St', city: 'Yaoundé', postalCode: '12345', country: 'Cameroon' }, dateOfBirth: '1990-01-01', nationality: 'Cameroonian', profession: 'Engineer', emergencyContact: { name: 'Jane Doe', phone: '+237699887777', relation: 'Sister' }, profilePhoto: undefined }, accountInfo: { role: 'President', joinDate: '2022-01-01', membershipNumber: 'MEM001', status: 'Actif', lastLoginDate: '2023-01-01' }, berryPoints: { totalPoints: 450, pointsHistory: [ { date: '2024-01-01', points: 50, reason: 'Cotisation mensuelle', type: 'gain' }, { date: '2024-02-01', points: 75, reason: 'Participation réunion', type: 'gain' }, { date: '2024-03-01', points: -25, reason: 'Retard cotisation', type: 'loss' } ], lastUpdate: '2024-01-01', eligibilityLevel: 'Gold', monthlyContributions: 25000, attendanceRate: 85 }, financial: { totalContributions: 100000, currentBalance: 50000, loanEligibility: 300000, activeLoans: [ { loanId: 'L001', amount: 150000, status: 'active', startDate: '2024-01-01', monthlyPayment: 15000 } ], contributionHistory: [ { date: '2024-01-01', amount: 25000, status: 'paid', berryPointsEarned: 50 }, { date: '2024-02-01', amount: 25000, status: 'paid', berryPointsEarned: 50 }, { date: '2024-03-01', amount: 25000, status: 'overdue', penalties: 2000 } ], sanctions: [], monthlyPenalties: 0 }, contributionStatus: 'à jour', loanHistory: ['L001'], isActive: true },
          { id: '2', personalInfo: { firstName: 'Jane', lastName: 'Smith', email: 'jane.smith@example.com', phone: '+237677665544', address: { street: '456 Elm St', city: 'Douala', postalCode: '54321', country: 'Cameroon' }, dateOfBirth: '1985-05-05', nationality: 'Cameroonian', profession: 'Doctor', emergencyContact: { name: 'John Smith', phone: '+237677665555', relation: 'Husband' } }, accountInfo: { role: 'Treasurer', joinDate: '2022-03-15', membershipNumber: 'MEM002', status: 'Actif', lastLoginDate: '2023-01-01' }, berryPoints: { totalPoints: 320, pointsHistory: [ { date: '2024-01-01', points: 40, reason: 'Cotisation mensuelle', type: 'gain' }, { date: '2024-02-01', points: 60, reason: 'Participation + bonus', type: 'gain' } ], lastUpdate: '2024-01-01', eligibilityLevel: 'Gold', monthlyContributions: 20000, attendanceRate: 75 }, financial: { totalContributions: 80000, currentBalance: 30000, loanEligibility: 300000, activeLoans: [], contributionHistory: [ { date: '2024-01-01', amount: 20000, status: 'paid', berryPointsEarned: 40 }, { date: '2024-02-01', amount: 20000, status: 'overdue', penalties: 1000 } ], sanctions: [ { id: 'S001', date: '2024-02-15', type: 'penalty', reason: 'Retard cotisation', amount: 1000, status: 'active' } ], monthlyPenalties: 1000 }, contributionStatus: 'en retard', loanHistory: [], isActive: true },
          { id: '3', personalInfo: { firstName: 'Paul', lastName: 'Atanga', email: 'paul.atanga@example.com', phone: '+237655443322', address: { street: '789 Pine St', city: 'Bamenda', postalCode: '67890', country: 'Cameroon' }, dateOfBirth: '1992-09-09', nationality: 'Cameroonian', profession: 'Teacher', emergencyContact: { name: 'Mary Atanga', phone: '+237655443333', relation: 'Mother' } }, accountInfo: { role: 'Member', joinDate: '2023-02-10', membershipNumber: 'MEM003', status: 'Exclu', lastLoginDate: '2023-01-01' }, berryPoints: { totalPoints: 150, pointsHistory: [ { date: '2024-01-01', points: 30, reason: 'Cotisation mensuelle', type: 'gain' }, { date: '2024-02-01', points: -50, reason: 'Sanction exclusion', type: 'loss' } ], lastUpdate: '2024-01-01', eligibilityLevel: 'Silver', monthlyContributions: 15000, attendanceRate: 50 }, financial: { totalContributions: 20000, currentBalance: 10000, loanEligibility: 120000, activeLoans: [], contributionHistory: [ { date: '2024-01-01', amount: 15000, status: 'paid', berryPointsEarned: 30 } ], sanctions: [ { id: 'S002', date: '2024-02-01', type: 'exclusion', reason: 'Non-respect des règles', status: 'active' } ], monthlyPenalties: 0 }, contributionStatus: 'absent', loanHistory: [], isActive: false }
        ]);

      } catch (error) {
        console.error('Failed to fetch members:', error);
      } finally {
        setLoading(false);
      }
    };
    fetchMembers();
  }, []);

  const filteredMembers = useMemo(() => {
    return members
      .filter(m => m.personalInfo.firstName.toLowerCase().includes(searchTerm.toLowerCase()) || m.personalInfo.lastName.toLowerCase().includes(searchTerm.toLowerCase()))
      .filter(m => (roleFilter ? m.accountInfo.role === roleFilter : true))
      .filter(m => (statusFilter ? m.accountInfo.status === statusFilter : true));
  }, [members, searchTerm, roleFilter, statusFilter]);

  const openModal = (member: Member | null = null) => {
    if (!hasPermission('manage_members')) return;
    setEditingMember(member);
    setFormErrors({});
    setModalOpen(true);
  };

  const handleDelete = (id: string) => {
    if (!hasPermission('manage_members')) return;
    if (window.confirm('Êtes-vous sûr de vouloir supprimer ce membre ?')) {
      setMembers(prev => prev.filter(m => m.id !== id));
    }
  };

  const closeModal = () => {
    setModalOpen(false);
    setEditingMember(null);
    setFormErrors({});
    setSelectedCountryCode('+237');
    setLocalPhoneNumber('');
  };

  const validateEmailUnique = (email: string, id?: string) => {
    return !members.some(m => m.personalInfo.email === email && m.id !== id);
  };

  const validatePhone = (phone: string) => {
    // Validate full phone number including country code
    // Simple regex to check + followed by digits, min 8 to max 15 digits total
    const phoneRegex = /^\+\d{8,15}$/;
    return phoneRegex.test(phone);
  };

  const validateRoleLimits = (role: string, id?: string) => {
  if (!(role in roleLimits)) return true; // Vérifie que le rôle existe dans le dictionnaire
  const typedRole = role as MemberRole;   // Cast sécurisé vers MemberRole
  const count = members.filter(m => m.accountInfo.role === typedRole && m.id !== id).length;
  return count < roleLimits[typedRole];
};

  const handleSave = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    if (!editingMember) return;

    const errors: { [key: string]: string } = {};
    
    // Combine country code and local phone number for validation
    const fullPhone = selectedCountryCode + localPhoneNumber;
    
    if (!editingMember.personalInfo.firstName.trim()) errors.firstName = 'Le prénom est requis.';
    if (!editingMember.personalInfo.lastName.trim()) errors.lastName = 'Le nom est requis.';
    if (!editingMember.personalInfo.email.trim()) errors.email = 'L\'email est requis.';
    else if (!validateEmailUnique(editingMember.personalInfo.email, editingMember.id)) errors.email = 'L\'email doit être unique.';
    if (!localPhoneNumber.trim()) errors.phone = 'Le téléphone est requis.';
    else if (!validatePhone(fullPhone)) errors.phone = 'Le téléphone doit être au format valide.';
    if (!editingMember.accountInfo.role) errors.role = 'Le rôle est requis.';
    else if (!validateRoleLimits(editingMember.accountInfo.role, editingMember.id)) errors.role = `Limite atteinte pour le rôle ${editingMember.accountInfo.role}.`;

    setFormErrors(errors);
    if (Object.keys(errors).length > 0) return;

    // Combine country code and local phone number before saving
    const memberToSave = { ...editingMember, personalInfo: { ...editingMember.personalInfo, phone: fullPhone } };

    // Generate a temporary password for new member
    const generateTempPassword = () => {
      const chars = 'abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789!@#$%^&*()';
      let password = '';
      for (let i = 0; i < 12; i++) {
        password += chars.charAt(Math.floor(Math.random() * chars.length));
      }
      return password;
    };

    let tempPassword = '';
    if (!memberToSave.id) {
      tempPassword = generateTempPassword();
    }

    try {
      if (memberToSave.id) {
        // Update existing member API call
        // await api.updateMember(memberToSave);
        setMembers(prev => prev.map(m => (m.id === memberToSave.id ? { ...memberToSave } : m)));
      } else {
        // Create new member API call with tempPassword
        // await api.createMember({ ...memberToSave, tempPassword });
        const newMember = { ...memberToSave, id: Date.now().toString() };
        setMembers(prev => [...prev, newMember]);

        // Simulate sending email and WhatsApp with tempPassword
        // TODO: Implement backend API to send email and WhatsApp messages
        console.log(`Send email and WhatsApp to ${memberToSave.personalInfo.email} and phone ${fullPhone} with password: ${tempPassword}`);
      }
      closeModal();
    } catch (error) {
      console.error('Failed to save member:', error);
      // Optionally set form error here
    }
  };

  const handleInputChange = (field: keyof Member['personalInfo'] | keyof Member['accountInfo'], value: any) => {
    if (editingMember) {
      if (field === 'phone') {
        // We handle phone input separately with localPhoneNumber state
        setLocalPhoneNumber(value);
      } else if (field in editingMember.personalInfo) {
        setEditingMember(prev => prev ? { ...prev, personalInfo: { ...prev.personalInfo, [field]: value } } : null);
      } else if (field in editingMember.accountInfo) {
        setEditingMember(prev => prev ? { ...prev, accountInfo: { ...prev.accountInfo, [field]: value } } : null);
      }
    }
  };

  if (loading) return <p>Loading...</p>;
  if (!hasPermission('view_members')) return <p>Vous n'avez pas la permission de voir cette page.</p>;

  return (
    <div style={{ display: 'flex', minHeight: '100vh', fontFamily: 'Arial, sans-serif' }}>
      <aside style={{ width: '250px', backgroundColor: '#1e3a8a', color: 'white', padding: '24px 16px' }}>
        {/* Sidebar content from Dashboard */}
      </aside>

      <main style={{ flex: 1, backgroundColor: '#f3f4f6', padding: 24 }}>
        {/* Bouton retour*/}
        <button 
          onClick={() => navigate('/')} 
          style={{ 
            padding: '8px 16px', 
            backgroundColor: '#3b82f6', 
            color: 'white', 
            border: 'none', 
            borderRadius: 4,
            cursor: 'pointer',
            display: 'flex',
            alignItems: 'center',
            gap: 8
          }}
        >
          ← Retour au Dashboard
        </button>

        <h1 style={{ fontSize: 28, fontWeight: 'bold', marginBottom: 24 }}>Gestion des Membres / Members Management</h1>

        <div style={{ display: 'flex', gap: 16, marginBottom: 24, flexWrap: 'wrap' }}>
          <input
            type="text"
            placeholder="Rechercher par nom..."
            value={searchTerm}
            onChange={e => setSearchTerm(e.target.value)}
            style={{ padding: 8, borderRadius: 4, border: '1px solid #ccc', flex: 1 }}
          />
          <select title="Role" value={roleFilter} onChange={e => setRoleFilter(e.target.value)} style={{ padding: 8, borderRadius: 4 }}>
            <option value="">Tous les rôles</option>
            <option value="President">Président</option>
            <option value="Treasurer">Trésorier</option>
            <option value="Secrecom">Secrécom</option>
            <option value="Accountant">Comptable</option>
            <option value="Censor">Censeur</option>
            <option value="Member">Membre</option>
            <option value="Guest">Invité</option>
          </select>
          <select title="Status" value={statusFilter} onChange={e => setStatusFilter(e.target.value)} style={{ padding: 8, borderRadius: 4 }}>
            <option value="">Tous les statuts</option>
            <option value="Actif">Actif</option>
            <option value="Suspendu">Suspendu</option>
            <option value="Exclu">Exclu</option>
          </select>
          {hasPermission('manage_members') && (
            <button onClick={() => openModal()} style={{ padding: '8px 16px', backgroundColor: '#2563eb', color: 'white', border: 'none', borderRadius: 4 }}>
              Ajouter Membre
            </button>
          )}
        </div>
 
        <div style={{ overflowX: 'auto' }}>
          <table style={{ width: '100%', borderCollapse: 'collapse', backgroundColor: 'white' }}>
            <thead>
              <tr style={{ backgroundColor: '#3b82f6', color: 'white' }}>
                <th style={{ padding: 12, textAlign: 'left', borderBottom: '1px solid #ddd' }}>Nom</th>
                <th style={{ padding: 12, textAlign: 'left', borderBottom: '1px solid #ddd' }}>Email</th>
                <th style={{ padding: 12, textAlign: 'left', borderBottom: '1px solid #ddd' }}>Téléphone</th>
                <th style={{ padding: 12, textAlign: 'left', borderBottom: '1px solid #ddd' }}>Rôle</th>
                <th style={{ padding: 12, textAlign: 'left', borderBottom: '1px solid #ddd' }}>Statut</th>
                <th style={{ padding: 12, textAlign: 'left', borderBottom: '1px solid #ddd' }}>Points Berry</th>
                <th style={{ padding: 12, textAlign: 'left', borderBottom: '1px solid #ddd' }}>Niveau</th>
                <th style={{ padding: 12, textAlign: 'left', borderBottom: '1px solid #ddd' }}>Cotisation</th>
                <th style={{ padding: 12, textAlign: 'left', borderBottom: '1px solid #ddd' }}>Pouvoir d'emprunt</th>
                <th style={{ padding: 12, textAlign: 'left', borderBottom: '1px solid #ddd' }}>Pénalités</th>
                {hasPermission('manage_members') && <th style={{ padding: 12, textAlign: 'left', borderBottom: '1px solid #ddd' }}>Actions</th>}
              </tr>
            </thead>
            <tbody>
              {filteredMembers.map(member => (
                <tr key={member.id} style={{ backgroundColor: member.isActive ? 'white' : '#fef2f2' }}>
                  <td style={{ padding: 12, borderBottom: '1px solid #ddd' }}>
                    <div style={{ display: 'flex', alignItems: 'center', gap: 8 }}>
                      <div style={{ 
                        width: 32, 
                        height: 32, 
                        borderRadius: '50%', 
                        backgroundColor: '#e5e7eb', 
                        display: 'flex', 
                        alignItems: 'center', 
                        justifyContent: 'center',
                        fontSize: 14,
                        fontWeight: 'bold'
                      }}>
                        {member.personalInfo.firstName[0]}{member.personalInfo.lastName[0]}
                      </div>
                      <div>
                        <div style={{ fontWeight: 'bold' }}>
                          {member.personalInfo.firstName} {member.personalInfo.lastName}
                        </div>
                        <div style={{ fontSize: 12, color: '#6b7280' }}>
                          {member.accountInfo.membershipNumber}
                        </div>
                      </div>
                    </div>
                  </td>
                  <td style={{ padding: 12, borderBottom: '1px solid #ddd' }}>
                    {member.personalInfo.email}
                  </td>
                  <td style={{ padding: 12, borderBottom: '1px solid #ddd' }}>
                    {member.personalInfo.phone}
                  </td>
                  <td style={{ padding: 12, borderBottom: '1px solid #ddd' }}>
                    <span style={{ 
                      padding: '4px 8px',
                      borderRadius: 4,
                      fontSize: 12,
                      fontWeight: 'bold',
                      backgroundColor: member.accountInfo.role === 'President' ? '#dbeafe' : 
                                    member.accountInfo.role === 'Treasurer' ? '#dcfce7' : '#f3f4f6',
                      color: member.accountInfo.role === 'President' ? '#1e40af' : 
                            member.accountInfo.role === 'Treasurer' ? '#166534' : '#374151'
                    }}>
                      {member.accountInfo.role}
                    </span>
                  </td>
                  <td style={{ padding: 12, borderBottom: '1px solid #ddd' }}>
                    <span style={{ 
                      padding: '4px 8px',
                      borderRadius: 4,
                      fontSize: 12,
                      fontWeight: 'bold',
                      backgroundColor: member.accountInfo.status === 'Actif' ? '#dcfce7' : 
                                    member.accountInfo.status === 'Suspendu' ? '#fef3c7' : '#fecaca',
                      color: member.accountInfo.status === 'Actif' ? '#166534' : 
                            member.accountInfo.status === 'Suspendu' ? '#92400e' : '#dc2626'
                    }}>
                      {member.accountInfo.status}
                    </span>
                  </td>
                  <td style={{ padding: 12, borderBottom: '1px solid #ddd' }}>
                    <div style={{ fontWeight: 'bold' }}>{member.berryPoints.totalPoints}</div>
                    <div style={{ fontSize: 12, color: '#6b7280' }}>
                      {member.berryPoints.pointsHistory.length > 0 && 
                        (member.berryPoints.pointsHistory[member.berryPoints.pointsHistory.length - 1].type === 'gain' ? '+' : '')
                      }
                      {member.berryPoints.pointsHistory.length > 0 && 
                        member.berryPoints.pointsHistory[member.berryPoints.pointsHistory.length - 1].points
                      }
                    </div>
                  </td>
                  <td style={{ padding: 12, borderBottom: '1px solid #ddd' }}>
                    <span style={{ 
                      padding: '4px 8px',
                      borderRadius: 4,
                      fontSize: 12,
                      fontWeight: 'bold',
                      backgroundColor: member.berryPoints.eligibilityLevel === 'Platinum' ? '#f3e8ff' : 
                                    member.berryPoints.eligibilityLevel === 'Gold' ? '#fef3c7' : 
                                    member.berryPoints.eligibilityLevel === 'Silver' ? '#f1f5f9' : '#fef2f2',
                      color: member.berryPoints.eligibilityLevel === 'Platinum' ? '#7c3aed' : 
                            member.berryPoints.eligibilityLevel === 'Gold' ? '#92400e' : 
                            member.berryPoints.eligibilityLevel === 'Silver' ? '#475569' : '#dc2626'
                    }}>
                      {member.berryPoints.eligibilityLevel}
                    </span>
                  </td>
                  <td style={{ padding: 12, borderBottom: '1px solid #ddd' }}>
                    <span style={{ 
                      padding: '4px 8px',
                      borderRadius: 4,
                      fontSize: 12,
                      fontWeight: 'bold',
                      backgroundColor: member.contributionStatus === 'à jour' ? '#dcfce7' : 
                                    member.contributionStatus === 'en retard' ? '#fef3c7' : '#fecaca',
                      color: member.contributionStatus === 'à jour' ? '#166534' : 
                            member.contributionStatus === 'en retard' ? '#92400e' : '#dc2626'
                    }}>
                      {member.contributionStatus}
                    </span>
                  </td>
                  <td style={{ padding: 12, borderBottom: '1px solid #ddd' }}>
                    <div style={{ fontWeight: 'bold' }}>
                      {member.financial.loanEligibility.toLocaleString()} XAF
                    </div>
                    <div style={{ fontSize: 12, color: '#6b7280' }}>
                      {member.financial.activeLoans.length} prêt(s) actif(s)
                    </div>
                  </td>
                  <td style={{ padding: 12, borderBottom: '1px solid #ddd' }}>
                    <div style={{ 
                      fontWeight: 'bold',
                      color: member.financial.monthlyPenalties > 0 ? '#dc2626' : '#166534'
                    }}>
                      {member.financial.monthlyPenalties.toLocaleString()} XAF
                    </div>
                    <div style={{ fontSize: 12, color: '#6b7280' }}>
                      {member.financial.sanctions.length} sanction(s)
                    </div>
                  </td>
                  {hasPermission('manage_members') && (
                    <td style={{ padding: 12, borderBottom: '1px solid #ddd' }}>
                      <div style={{ display: 'flex', gap: 8 }}>
                        <button 
                          onClick={() => openModal(member)} 
                          style={{ 
                            padding: '6px 12px', 
                            border: 'none', 
                            borderRadius: 4, 
                            cursor: 'pointer', 
                            backgroundColor: '#3b82f6', 
                            color: 'white',
                            fontSize: 12
                          }}
                        >
                          Modifier
                        </button>
                        <button
                          onClick={() => handleDelete(member.id)}
                          style={{ 
                            padding: '6px 12px', 
                            border: 'none', 
                            borderRadius: 4, 
                            cursor: 'pointer', 
                            backgroundColor: '#ef4444', 
                            color: 'white',
                            fontSize: 12
                          }}
                          aria-label={`Supprimer ${member.personalInfo.firstName} ${member.personalInfo.lastName}`}
                        >
                          Supprimer
                        </button>
                      </div>
                    </td>
                  )}
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        {isModalOpen && (
          <div style={{
            position: 'fixed', top: 0, left: 0, right: 0, bottom: 0,
            backgroundColor: 'rgba(0,0,0,0.5)', display: 'flex',
            alignItems: 'center', justifyContent: 'center',
            zIndex: 1000
          }}>
            <div style={{ backgroundColor: 'white', padding: 24, borderRadius: 8, width: '400px', maxHeight: '80vh', overflowY: 'auto' }}>
              <h2 style={{ marginBottom: 16 }}>{editingMember?.id ? 'Modifier Membre' : 'Ajouter Membre'}</h2>
              <form onSubmit={handleSave}>
                <div style={{ marginBottom: 12 }}>
                  <label htmlFor="firstName">Prénom</label><br />
                  <input
                    id="firstName"
                    type="text"
                    value={editingMember?.personalInfo.firstName || ''}
                    onChange={e => handleInputChange('firstName', e.target.value)}
                    style={{ width: '100%', padding: 8, borderRadius: 4, border: formErrors.firstName ? '1px solid red' : '1px solid #ccc' }}
                  />
                  {formErrors.firstName && <div style={{ color: 'red', fontSize: 12 }}>{formErrors.firstName}</div>}
                </div>
                <div style={{ marginBottom: 12 }}>
                  <label htmlFor="lastName">Nom</label><br />
                  <input
                    id="lastName"
                    type="text"
                    value={editingMember?.personalInfo.lastName || ''}
                    onChange={e => handleInputChange('lastName', e.target.value)}
                    style={{ width: '100%', padding: 8, borderRadius: 4, border: formErrors.lastName ? '1px solid red' : '1px solid #ccc' }}
                  />
                  {formErrors.lastName && <div style={{ color: 'red', fontSize: 12 }}>{formErrors.lastName}</div>}
                </div>
                <div style={{ marginBottom: 12 }}>
                  <label htmlFor="email">Email</label><br />
                  <input
                    id="email"
                    type="email"
                    value={editingMember?.personalInfo.email || ''}
                    onChange={e => handleInputChange('email', e.target.value)}
                    style={{ width: '100%', padding: 8, borderRadius: 4, border: formErrors.email ? '1px solid red' : '1px solid #ccc' }}
                  />
                  {formErrors.email && <div style={{ color: 'red', fontSize: 12 }}>{formErrors.email}</div>}
                </div>
                <div style={{ marginBottom: 12 }}>
                  <label htmlFor="phone">Téléphone</label><br />
                  <div style={{ display: 'flex', gap: 8 }}>
                    <select
                      id="countryCode"
                      value={selectedCountryCode}
                      onChange={e => setSelectedCountryCode(e.target.value)}
                      style={{ padding: 8, borderRadius: 4, border: '1px solid #ccc', minWidth: '120px' }}
                      aria-label="Code pays"
                    >
                      {countryCodes.map(c => (
                        <option key={c.code} value={c.code}>{c.label}</option>
                      ))}
                    </select>
                    <input
                      id="phone"
                      type="text"
                      value={localPhoneNumber}
                      onChange={e => setLocalPhoneNumber(e.target.value)}
                      placeholder="Ex: 699887766"
                      style={{ flex: 1, padding: 8, borderRadius: 4, border: formErrors.phone ? '1px solid red' : '1px solid #ccc' }}
                    />
                  </div>
                  {formErrors.phone && <div style={{ color: 'red', fontSize: 12 }}>{formErrors.phone}</div>}
                </div>
                <div style={{ marginBottom: 12 }}>
                  <label htmlFor="role">Rôle</label><br />
                  <select
                    id="role"
                    value={editingMember?.accountInfo.role || ''}
                    onChange={e => handleInputChange('role', e.target.value)}
                    style={{ width: '100%', padding: 8, borderRadius: 4, border: formErrors.role ? '1px solid red' : '1px solid #ccc' }}
                  >
                    <option value="">Sélectionnez un rôle</option>
                    <option value="President">Président</option>
                    <option value="Treasurer">Trésorier</option>
                    <option value="Secrecom">Secrécom</option>
                    <option value="Accountant">Comptable</option>
                    <option value="Censor">Censeur</option>
                    <option value="Member">Membre</option>
                    <option value="Guest">Invité</option>
                  </select>
                  {formErrors.role && <div style={{ color: 'red', fontSize: 12 }}>{formErrors.role}</div>}
                </div>
                <div style={{ display: 'flex', justifyContent: 'flex-end', gap: 8, marginTop: 16 }}>
                  <button type="button" onClick={closeModal} style={{ padding: '8px 16px', border: '1px solid #ccc', borderRadius: 4, backgroundColor: 'white' }}>Annuler</button>
                  <button type="submit" style={{ padding: '8px 16px', backgroundColor: '#2563eb', color: 'white', border: 'none', borderRadius: 4 }}>
                    Sauvegarder
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}
      </main>
    </div>
  );
};

export default Members;
