// src/components/Members.tsx

import React, { useState, useEffect, useMemo, ReactNode, InputHTMLAttributes, SelectHTMLAttributes, TextareaHTMLAttributes, ChangeEvent } from 'react';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlus, faPen, faTrash } from '@fortawesome/free-solid-svg-icons';

// --- HOOK POUR LA RESPONSIVITÉ ---
const useWindowSize = () => {
    const [size, setSize] = useState([window.innerWidth]);
    useEffect(() => {
        const handleResize = () => setSize([window.innerWidth]);
        window.addEventListener('resize', handleResize);
        return () => window.removeEventListener('resize', handleResize);
    }, []);
    return { width: size[0] };
};

// --- INTERFACES & CONSTANTES ---
interface Address { street: string; city: string; postalCode: string; country: string; }
interface EmergencyContact { name: string; phone: string; relation: string; }
// MODIFICATION: Ajout du rôle 'Admin' pour correspondre au backend
type MemberRole = 'Admin' | 'President' | 'Treasurer' | 'Secrecom' | 'Accountant' | 'Censor' | 'Member' | 'Guest';
type MemberStatus = 'Actif' | 'Suspendu' | 'Exclu';
interface Member {
  id: string;
  personalInfo: { firstName: string; lastName: string; email: string; phone: string; address: Address; dateOfBirth: string; nationality: string; profession: string; emergencyContact: EmergencyContact; };
  accountInfo: { role: MemberRole; joinDate: string; membershipNumber: string; status: MemberStatus; };
  berryPoints: { totalPoints: number; eligibilityLevel: 'Bronze' | 'Silver' | 'Gold' | 'Platinum'; };
  financial: { loanEligibility: number; };
