// --- COMPOSANT PRINCIPAL ---
const Members: React.FC = () => {
  const { user, hasPermission } = useAuth();
  const { width } = useWindowSize();
  const isMobile = width < MOBILE_BREAKPOINT;

  const [members, setMembers] = useState<Member[]>(initialMembersData);
  const [searchTerm, setSearchTerm] = useState('');
  const [roleFilter, setRoleFilter] = useState('');
  const [statusFilter, setStatusFilter] = useState('');
  const [isModalOpen, setModalOpen] = useState(false);
  const [editingMember, setEditingMember] = useState<Member | null>(null);
  const [formErrors, setFormErrors] = useState<{ [key: string]: string }>({});
  
  const filteredMembers = useMemo(() => {
    return members
      .filter(m => `${m.personalInfo.firstName} ${m.personalInfo.lastName}`.toLowerCase().includes(searchTerm.toLowerCase()))
      .filter(m => (roleFilter ? m.accountInfo.role === roleFilter : true))
      .filter(m => (statusFilter ? m.accountInfo.status === statusFilter : true));
  }, [members, searchTerm, roleFilter, statusFilter]);

  const openModal = (member: Member | null = null) => {
    if (!hasPermission('manage_members')) return alert("Action non autorisée.");
    setEditingMember(member ? { ...member } : { ...newMemberTemplate, id: '' });
    setFormErrors({});
    setModalOpen(true);
  };
  const closeModal = () => { setModalOpen(false); setEditingMember(null); };

  const handleDelete = (id: string) => {
    if (!hasPermission('manage_members')) return alert("Action non autorisée.");
    if (window.confirm('Êtes-vous sûr de vouloir supprimer ce membre ?')) {
      setMembers(prev => prev.filter(m => m.id !== id));
    }
  };

  const handleSave = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    if (!editingMember) return;
    // ... (Votre logique de validation est conservée)
    if (editingMember.id) {
        setMembers(prev => prev.map(m => (m.id === editingMember.id ? editingMember : m)));
    } else {
        setMembers(prev => [...prev, { ...editingMember, id: Date.now().toString() }]);
    }
    closeModal();
  };

  const handleInputChange = (section: 'personalInfo' | 'accountInfo', field: string, value: any) => {
    if (editingMember) {
      setEditingMember(prev => prev ? { ...prev, [section]: { ...prev[section], [field]: value } } : null);
    }
  };

  if (!hasPermission('view_members')) {
    return <div style={styles.page}><p>Vous n'avez pas la permission de voir cette page.</p></div>;
  }

  return (
    <>
      <header style={styles.header}>
        <h2 style={styles.headerTitle}>Gestion des Membres</h2>
        {hasPermission('manage_members') && (
            <button onClick={() => openModal()} style={styles.button}>
                <FontAwesomeIcon icon={faPlus} style={{ marginRight: '8px' }} /> Ajouter un Membre
            </button>
        )}
      </header>
      
      <div style={styles.controlsSection}>
        <input type="text" placeholder="Rechercher par nom..." value={searchTerm} onChange={e => setSearchTerm(e.target.value)} style={styles.searchInput} />
        {/* MODIFICATION: Ajout de l'option Admin dans le filtre */}
        <select value={roleFilter} onChange={e => setRoleFilter(e.target.value)} style={styles.filterSelect}>
          <option value="">Tous les rôles</option>
          <option value="Admin">Administrateur</option>
          <option value="President">Président</option>
          <option value="Treasurer">Trésorier</option>
          <option value="Secrecom">Secrécom</option>
          <option value="Accountant">Comptable</option>
          <option value="Censor">Censeur</option>
          <option value="Member">Membre</option>
          <option value="Guest">Invité</option>
        </select>
        <select value={statusFilter} onChange={e => setStatusFilter(e.target.value)} style={styles.filterSelect}><option value="">Tous les statuts</option><option value="Actif">Actif</option><option value="Suspendu">Suspendu</option><option value="Exclu">Exclu</option></select>
      </div>

      {isMobile ? 
        <MobileMemberList members={filteredMembers} onEdit={openModal} onDelete={handleDelete} hasPermission={hasPermission} /> : 
        <DesktopMemberTable members={filteredMembers} onEdit={openModal} onDelete={handleDelete} hasPermission={hasPermission} />
      }

      {isModalOpen && editingMember && (
        <div style={styles.modalOverlay}>
            <div style={styles.modalContent}>
                <h3 style={styles.modalTitle}>{editingMember.id ? 'Modifier le Membre' : 'Ajouter un Nouveau Membre'}</h3>
                <form onSubmit={handleSave} style={styles.modalForm}>
                    <ModalInput label="Prénom" value={editingMember.personalInfo.firstName} onChange={(e) => handleInputChange('personalInfo', 'firstName', e.target.value)} error={formErrors.firstName} />
                    <ModalInput label="Nom" value={editingMember.personalInfo.lastName} onChange={(e) => handleInputChange('personalInfo', 'lastName', e.target.value)} error={formErrors.lastName} />
                    <ModalInput label="Email" type="email" value={editingMember.personalInfo.email} onChange={(e) => handleInputChange('personalInfo', 'email', e.target.value)} error={formErrors.email} />
                    <ModalInput label="Téléphone" value={editingMember.personalInfo.phone} onChange={(e) => handleInputChange('personalInfo', 'phone', e.target.value)} error={formErrors.phone} />
                    {/* MODIFICATION: Ajout de l'option Admin dans le modal */}
                    <ModalSelect label="Rôle" value={editingMember.accountInfo.role} onChange={(e) => handleInputChange('accountInfo', 'role', e.target.value)} error={formErrors.role}>
                        <option value="Admin">Administrateur</option>
                        <option value="President">Président</option>
                        <option value="Treasurer">Trésorier</option>
                        <option value="Secrecom">Secrécom</option>
                        <option value="Accountant">Comptable</option>
                        <option value="Censor">Censeur</option>
                        <option value="Member">Membre</option>
                        <option value="Guest">Invité</option>
                    </ModalSelect>
                    <ModalSelect label="Statut" value={editingMember.accountInfo.status} onChange={(e) => handleInputChange('accountInfo', 'status', e.target.value)}>
                        <option value="Actif">Actif</option><option value="Suspendu">Suspendu</option><option value="Exclu">Exclu</option>
                    </ModalSelect>
                    <div style={styles.modalActions}>
                      <button type="button" onClick={closeModal} style={{...styles.button, backgroundColor: '#6b7280'}}>Annuler</button>
                      <button type="submit" style={styles.button}>Sauvegarder</button>
                    </div>
                </form>
            </div>
        </div>
      )}
    </>
  );
};

