            <tbody>
                {members.map(member => (
                    <tr key={member.id}>
                        <td style={styles.td}><div style={{fontWeight: 'bold'}}>{member.personalInfo.firstName} {member.personalInfo.lastName}</div><div style={{fontSize: '12px', color: '#6b7280'}}>{member.accountInfo.membershipNumber}</div></td>
                        <td style={styles.td}>{member.personalInfo.email}<br/><span style={{fontSize: '12px', color: '#6b7280'}}>{member.personalInfo.phone}</span></td>
