                        <td style={styles.td}><StatusChip status={member.accountInfo.status} /></td>
                        <td style={styles.td}><div style={{fontWeight: 'bold', fontSize: '16px'}}>{member.berryPoints.totalPoints}</div><LevelChip level={member.berryPoints.eligibilityLevel} /></td>
                        <td style={styles.td}><ContributionChip status={member.contributionStatus} /></td>
                        {hasPermission('manage_members') && <td style={styles.td}>
                            <div style={{display: 'flex', gap: '8px'}}>
                                <button onClick={() => onEdit(member)} style={{...styles.actionButton, backgroundColor: '#3b82f6'}} title="Modifier"><FontAwesomeIcon icon={faPen}/></button>
                                <button onClick={() => onDelete(member.id)} style={{...styles.actionButton, backgroundColor: '#ef4444'}} title="Supprimer"><FontAwesomeIcon icon={faTrash}/></button>
                            </div>
                        </td>}
                    </tr>
                ))}
            </tbody>
        </table>
    </div>
);
const MobileMemberList: React.FC<{members: Member[], onEdit: (m: Member) => void, onDelete: (id: string) => void, hasPermission: any}> = ({ members, onEdit, onDelete, hasPermission }) => (
    <div style={styles.mobileList}>
        {members.map(member => (
            <div key={member.id} style={styles.mobileCard}>
