const StatusChip: React.FC<{ status: MemberStatus }> = ({ status }) => { const sC = { 'Actif': { backgroundColor: '#dcfce7', color: '#166534' }, 'Suspendu': { backgroundColor: '#fef3c7', color: '#92400e' }, 'Exclu': { backgroundColor: '#fee2e2', color: '#991b1b' } }; return <span style={{ ...styles.chip, ...sC[status] }}>{status}</span>; };

// MODIFICATION: Mise à jour de RoleChip pour inclure Admin
const RoleChip: React.FC<{ role: MemberRole }> = ({ role }) => {
    const rC: Record<MemberRole, string> = {
        'Admin': '#fee2e2', // Rouge clair pour Admin
        'President': '#dbeafe',
        'Treasurer': '#dcfce7',
        'Secrecom': '#e5e7eb',
        'Accountant': '#d1fae5',
        'Censor': '#fef3c7',
        'Member': '#f3f4f6',
        'Guest': '#e5e7eb'
    };
    return <span style={{ ...styles.chip, backgroundColor: rC[role] || '#e5e7eb', color: '#374151' }}>{role}</span>;
};

const LevelChip: React.FC<{ level: Member['berryPoints']['eligibilityLevel'] }> = ({ level }) => { const lC = { 'Platinum': '#f3e8ff', 'Gold': '#fef3c7', 'Silver': '#f1f5f9', 'Bronze': '#fef2f2' }; return <span style={{ ...styles.chip, backgroundColor: lC[level], color: '#374151' }}>{level}</span>; };
const ContributionChip: React.FC<{ status: Member['contributionStatus'] }> = ({ status }) => { const cC = { 'à jour': { backgroundColor: '#dcfce7', color: '#166534' }, 'en retard': { backgroundColor: '#fef3c7', color: '#92400e' }, 'absent': { backgroundColor: '#fee2e2', color: '#991b1b' } }; return <span style={{ ...styles.chip, ...cC[status] }}>{status}</span>; };
interface ModalInputProps extends InputHTMLAttributes<HTMLInputElement> { label: string; error?: string; }
const ModalInput: React.FC<ModalInputProps> = ({ label, error, ...props }) => (<div><label style={styles.modalLabel}>{label}</label><input style={{...styles.modalInput, ...(error && styles.inputError)}} {...props} />{error && <p style={styles.errorMessage}>{error}</p>}</div>);
interface ModalSelectProps extends SelectHTMLAttributes<HTMLSelectElement> { label: string; error?: string; children: ReactNode; }
const ModalSelect: React.FC<ModalSelectProps> = ({ label, error, children, ...props }) => (<div><label style={styles.modalLabel}>{label}</label><select style={{...styles.modalInput, ...(error && styles.inputError)}} {...props}>{children}</select>{error && <p style={styles.errorMessage}>{error}</p>}</div>);

