// src/components/LoginPage.tsx

import React, { useState } from 'react';
import { useLocation, useNavigate, Link } from 'react-router-dom'; // Importez useLocation et Link
import { useAuth } from '../context/AuthContext';

const LoginPage: React.FC = () => {
    const navigate = useNavigate();
    const location = useLocation(); // Pour r√©cup√©rer le message de succ√®s
    const { login } = useAuth();

    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');

    // R√©cup√©rer le message de succ√®s s'il existe
    const successMessage = location.state?.message;

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        setError('');
        const success = await login(username, password);
        if (success) {
            navigate('/');
        } else {
            setError('Nom d\'utilisateur ou mot de passe incorrect.');
        }
    };

    // ... (votre JSX pour LoginPage)
    // Assurez-vous d'afficher 'successMessage' et 'error'
    // Voici un exemple complet que vous pouvez adapter :
    return (
        <div style={styles.container}>
            <div style={styles.formWrapper}>
                <div style={styles.header}>
                    <span style={styles.logo}>üè¶</span>
                    <h1 style={styles.title}>Connexion</h1>
                    <p style={styles.subtitle}>Acc√©dez √† votre tableau de bord Friendly Banks.</p>
                </div>

                {successMessage && <p style={styles.successMessage}>{successMessage}</p>}
                {error && <p style={styles.apiError}>{error}</p>}
                
                <form onSubmit={handleSubmit} style={styles.form}>
                    <input type="text" value={username} onChange={e => setUsername(e.target.value)} placeholder="Nom d'utilisateur" style={styles.input} />
                    <input type="password" value={password} onChange={e => setPassword(e.target.value)} placeholder="Mot de passe" style={styles.input} />
                    <button type="submit" style={styles.submitButton}>Se connecter</button>
                </form>

                <p style={styles.footerText}>
                    Vous n'avez pas de compte ? <Link to="/signup" style={styles.link}>Inscrivez-vous</Link>
                </p>
            </div>
        </div>
    );
};

// Styles (similaires √† SignUpPage pour la coh√©rence)
const styles: { [key: string]: React.CSSProperties } = {
    container: { display: 'flex', alignItems: 'center', justifyContent: 'center', minHeight: '100vh', backgroundColor: '#f3f4f6' },
    formWrapper: { width: '100%', maxWidth: '450px', backgroundColor: 'white', padding: '40px', borderRadius: '12px', boxShadow: '0 10px 25px rgba(0,0,0,0.1)' },
    header: { textAlign: 'center', marginBottom: '32px' },
    logo: { fontSize: '40px' },
    title: { fontSize: '28px', fontWeight: 'bold', color: '#1f2937', margin: '16px 0 8px 0' },
    subtitle: { fontSize: '16px', color: '#6b7280', margin: 0 },
    form: { display: 'flex', flexDirection: 'column', gap: '20px' },
    input: { width: '100%', padding: '12px', border: '1px solid #d1d5db', borderRadius: '8px', fontSize: '16px', boxSizing: 'border-box' },
    apiError: { color: '#ef4444', backgroundColor: '#fee2e2', padding: '12px', borderRadius: '8px', textAlign: 'center' },
    successMessage: { color: '#166534', backgroundColor: '#dcfce7', padding: '12px', borderRadius: '8px', textAlign: 'center' },
    submitButton: { padding: '12px', backgroundColor: '#2563eb', color: 'white', border: 'none', borderRadius: '8px', cursor: 'pointer', fontWeight: '600', fontSize: '16px' },
    footerText: { textAlign: 'center', marginTop: '24px', color: '#6b7280' },
    link: { color: '#2563eb', textDecoration: 'none', fontWeight: '600' },
};

export default LoginPage;
