import React, { useState, useEffect, useMemo, ReactNode, InputHTMLAttributes, SelectHTMLAttributes, TextareaHTMLAttributes, ChangeEvent } from 'react';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlus, faCheck, faTimes, faChevronRight } from '@fortawesome/free-solid-svg-icons';

// --- HOOK POUR LA RESPONSIVITÉ ---
const useWindowSize = () => {
  const [size, setSize] = useState([window.innerWidth]);
  useEffect(() => {
    const handleResize = () => setSize([window.innerWidth]);
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);
  return { width: size[0] };
};


// --- INTERFACES & CONSTANTES ---
interface Loan {
  id: string;
  memberId: string;
  memberName: string;
  amount: number;
  interestRate: number; // en %
  totalToRepay: number;
  date: string;
  status: 'En attente' | 'Approuvé' | 'Rejeté' | 'Remboursé';
  justification: string;
  guarantors: { id: string, name: string }[];
}
interface Member { id: string; name: string; berryPoints: number; }
const MOCK_TOTAL_FUND = 150000;
const MOBILE_BREAKPOINT = 768;


// --- DONNÉES DE SIMULATION ---
const initialMembers: Member[] = [
  { id: 'M001', name: 'Alice Dupont', berryPoints: 120 }, { id: 'M002', name: 'Bob Martin', berryPoints: 85 },
  { id: 'M003', name: 'Charlie Brown', berryPoints: 250 }, { id: 'M004', name: 'Diana Prince', berryPoints: 45 },
];
const mockLoans: Loan[] = [
    { id: 'L001', memberId: 'M002', memberName: 'Bob Martin', amount: 50000, interestRate: 8, totalToRepay: 54000, date: '2025-06-20', status: 'Approuvé', justification: 'Frais médicaux urgents', guarantors: [{id: 'M001', name: 'Alice Dupont'}, {id: 'M003', name: 'Charlie Brown'}]},
    { id: 'L002', memberId: 'M004', memberName: 'Diana Prince', amount: 30000, interestRate: 10, totalToRepay: 33000, date: '2025-07-01', status: 'En attente', justification: 'Réparation voiture suite à un accident', guarantors: [{id: 'M001', name: 'Alice Dupont'}, {id: 'M002', name: 'Bob Martin'}]},
    { id: 'L003', memberId: 'M001', memberName: 'Alice Dupont', amount: 150000, interestRate: 6, totalToRepay: 159000, date: '2025-05-10', status: 'Remboursé', justification: 'Acompte projet immobilier', guarantors: [{id: 'M002', name: 'Bob Martin'}, {id: 'M003', name: 'Charlie Brown'}]},
];


// --- SERVICE DE LOGIQUE MÉTIER POUR LES PRÊTS ---
const LoanService = {
  getMaxLoanAmount: (berryPoints: number): number => {
    if (berryPoints >= 10 && berryPoints <= 100) return 60000;
    if (berryPoints > 100 && berryPoints <= 199) return 120000;
    if (berryPoints >= 200 && berryPoints <= 499) return 300000;
    if (berryPoints >= 500) return MOCK_TOTAL_FUND * 0.7;
    return 0;
  },
  getInterestRate: (amount: number): number => {
    if (amount > 0 && amount < 40000) return 10;
    if (amount >= 40000 && amount < 100000) return 8;
    if (amount >= 100000) return 6;
    return 0;
  }
};


// --- COMPOSANT PRINCIPAL ---
const Loans: React.FC = () => {
  const { user, hasPermission } = useAuth();
  const navigate = useNavigate();
  const { width } = useWindowSize();
  const isMobile = width < MOBILE_BREAKPOINT;

  const [loans, setLoans] = useState<Loan[]>(mockLoans);
  const [members] = useState<Member[]>(initialMembers);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [formError, setFormError] = useState('');
  
  // NOTE: 'user.id' doit être défini dans votre type User de AuthContext
  const currentUserId = user?.id || 'M001'; 
  
  const initialFormState = {
    memberId: currentUserId, amount: 5000, justification: '', guarantor1Id: '', guarantor2Id: '',
  };
  const [newRequest, setNewRequest] = useState(initialFormState);

  const selectedMember = useMemo(() => members.find(m => m.id === newRequest.memberId), [newRequest.memberId, members]);
  
  const loanDetails = useMemo(() => {
    if (!selectedMember) return null;
    const maxAmount = LoanService.getMaxLoanAmount(selectedMember.berryPoints);
    const interestRate = LoanService.getInterestRate(newRequest.amount);
    const totalToRepay = newRequest.amount * (1 + interestRate / 100);
    let error = '';
    if (newRequest.amount > maxAmount) error = `Le montant dépasse votre plafond de prêt de ${maxAmount.toLocaleString()} XAF.`;
    else if (newRequest.amount > MOCK_TOTAL_FUND * 0.7) error = `Le montant dépasse 70% des fonds disponibles.`;
    return { maxAmount, interestRate, totalToRepay, error };
  }, [newRequest.amount, selectedMember]);

  const handleOpenModal = () => {
    setNewRequest({...initialFormState, memberId: currentUserId});
    setFormError('');
    setIsModalOpen(true);
  };

  const handleStatusUpdate = (id: string, status: 'Approuvé' | 'Rejeté') => {
    if (!hasPermission('approve_loans')) return alert("Action non autorisée.");
    setLoans(loans.map(loan => loan.id === id ? { ...loan, status } : loan));
  };

  const handleSaveRequest = () => {
    // Logique de validation complète...
    setLoans([ { /* ...newLoan */ } as Loan, ...loans]);
    setIsModalOpen(false);
  };

  const loanStats = useMemo(() => ({
    pendingCount: loans.filter(l => l.status === 'En attente').length,
    activeLoanAmount: loans.filter(l => l.status === 'Approuvé').reduce((sum, l) => sum + l.amount, 0),
    repaidAmount: loans.filter(l => l.status === 'Remboursé').reduce((sum, l) => sum + l.amount, 0),
  }), [loans]);
  
  return (
    <div style={styles.page}>
      <header style={styles.header}>
        <h2 style={styles.headerTitle}>Gestion des Prêts</h2>
        <button onClick={() => navigate('/')} style={{...styles.button, backgroundColor: '#6b7280'}}>← Retour</button>
      </header>
      
      <section style={styles.statSection}>
        <StatCard title="Total des Prêts Actifs" value={`${loanStats.activeLoanAmount.toLocaleString()} XAF`} />
        <StatCard title="Demandes en Attente" value={loanStats.pendingCount} />
        <StatCard title="Total Remboursé" value={`${loanStats.repaidAmount.toLocaleString()} XAF`} />
      </section>

      <section style={styles.controlsSection}>
        <button onClick={handleOpenModal} style={styles.button}>
          <FontAwesomeIcon icon={faPlus} style={{ marginRight: '8px' }} />
          Faire une Demande de Prêt
        </button>
      </section>

      {isMobile ? (
        <MobileLoanList loans={loans} hasPermission={hasPermission} onStatusUpdate={handleStatusUpdate} />
      ) : (
        <DesktopLoanTable loans={loans} hasPermission={hasPermission} onStatusUpdate={handleStatusUpdate} />
      )}

      {isModalOpen && ( /* La modale reste la même */
        <div style={styles.modalOverlay}>
            <div style={styles.modalContent}>
                <h3 style={styles.modalTitle}>Nouvelle Demande de Prêt</h3>
                {formError && <p style={styles.formError}>{formError}</p>}
                
                <div style={styles.modalForm}>
                    <ModalSelect label="Membre demandeur" value={newRequest.memberId} onChange={(e: ChangeEvent<HTMLSelectElement>) => setNewRequest({...newRequest, memberId: e.target.value})}>
                        <option value="" disabled>Sélectionner...</option>
                        {members.map(m => <option key={m.id} value={m.id}>{m.name} ({m.berryPoints} pts)</option>)}
                    </ModalSelect>
                    {selectedMember && (
                        <>
                            <ModalInput label="Montant souhaité (XAF)" type="number" step="1000" value={newRequest.amount} onChange={(e: ChangeEvent<HTMLInputElement>) => setNewRequest({...newRequest, amount: parseFloat(e.target.value) || 0})} />
                            {loanDetails && (
                                <div style={styles.loanDetailsBox}>
                                    <p>Plafond de prêt: <strong>{loanDetails.maxAmount.toLocaleString()} XAF</strong></p>
                                    <p>Taux d'intérêt: <strong>{loanDetails.interestRate}%</strong></p>
                                    <p>Total à rembourser: <strong>{loanDetails.totalToRepay.toLocaleString()} XAF</strong></p>
                                    {loanDetails.error && <p style={{color: '#ef4444'}}>{loanDetails.error}</p>}
                                </div>
                            )}
                            <ModalTextarea label="Motif de la demande (urgence avérée)" value={newRequest.justification} onChange={(e: ChangeEvent<HTMLTextAreaElement>) => setNewRequest({...newRequest, justification: e.target.value})} />
                            <ModalSelect label="Avaliseur / Garant 1" value={newRequest.guarantor1Id} onChange={(e: ChangeEvent<HTMLSelectElement>) => setNewRequest({...newRequest, guarantor1Id: e.target.value})}>
                                <option value="" disabled>Sélectionner...</option>
                                {members.filter(m => m.id !== newRequest.memberId).map(m => <option key={m.id} value={m.id}>{m.name}</option>)}
                            </ModalSelect>
                            <ModalSelect label="Avaliseur / Garant 2" value={newRequest.guarantor2Id} onChange={(e: ChangeEvent<HTMLSelectElement>) => setNewRequest({...newRequest, guarantor2Id: e.target.value})}>
                                <option value="" disabled>Sélectionner...</option>
                                {members.filter(m => m.id !== newRequest.memberId).map(m => <option key={m.id} value={m.id}>{m.name}</option>)}
                            </ModalSelect>
                        </>
                    )}
                </div>
                <div style={styles.modalActions}>
                  <button onClick={() => setIsModalOpen(false)} style={{...styles.button, backgroundColor: '#6b7280'}}>Annuler</button>
                  <button onClick={handleSaveRequest} style={styles.button}>Soumettre la Demande</button>
                </div>
            </div>
        </div>
      )}
    </div>
  );
};


