const StatusChip: React.FC<{ status: Loan['status'] }> = ({ status }) => {
    const statusColors = { 'En attente': { backgroundColor: '#fef3c7', color: '#92400e' }, 'Approuvé': { backgroundColor: '#dcfce7', color: '#166534' }, 'Rejeté': { backgroundColor: '#fee2e2', color: '#991b1b' }, 'Remboursé': { backgroundColor: '#e0e7ff', color: '#3730a3' } };
