import React from 'react';
import { useAuth } from '../context/AuthContext';

const Governance: React.FC = () => {
  const { user, hasPermission } = useAuth();

  if (!hasPermission('manage_rules')) {
    return <p>You do not have permission to view this page.</p>;
  }

  return (
    <div style={{
      padding: 20,
      maxWidth: 800,
      margin: 'auto',
      fontFamily: 'system-ui, -apple-system, sans-serif',
      color: '#1f2937',
    }}>
      <h1 style={{ fontSize: '28px', fontWeight: 'bold', marginBottom: '16px' }}>Governance and Meetings</h1>
      <p style={{ fontSize: '16px', marginBottom: '24px' }}>Welcome, {user?.username}. Here you can manage governance rules, meetings, and voting.</p>
      {/* TODO: Implement governance rules, meeting schedules, voting system */}
    </div>
  );
};

export default Governance;
