// src/App.tsx

import React from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import { AuthProvider, useAuth } from './context/AuthContext';

// Importez le nouveau Layout et toutes les pages
import Layout from './components/Layout'; 
import LoginPage from './components/LoginPage';
import Dashboard from './components/Dashboard';
import Members from './components/Members';
import Contributions from './components/Contributions';
import Loans from './components/Loans';
import Sanctions from './components/Sanctions';
import Governance from './components/Governance';

// Ce composant de garde reste le même, il est très bien.
const PrivateRoute = ({ children }: { children: React.ReactElement }) => {
  const { isAuthenticated } = useAuth();
  return isAuthenticated ? children : <Navigate to="/login" replace />;
};

const App: React.FC = () => {
  return (
    <AuthProvider>
      <Router>
        <Routes>
          {/* Route pour la page de connexion, qui n'a PAS de barre latérale */}
          <Route path="/login" element={<LoginPage />} />

          {/* Toutes les routes protégées sont maintenant des "enfants" du Layout */}
          <Route 
            path="/" 
            element={
              <PrivateRoute>
                <Layout />
              </PrivateRoute>
            }
          >
            {/* La route "index" est la page par défaut (Dashboard) */}
            <Route index element={<Dashboard />} />
            
            {/* Les autres pages qui s'afficheront à l'intérieur du Layout */}
            <Route path="members" element={<Members />} />
            <Route path="contributions" element={<Contributions />} />
            <Route path="loans" element={<Loans />} />
            <Route path="sanctions" element={<Sanctions />} />
            <Route path="governance" element={<Governance />} />
            
            {/* Ajoutez ici vos autres routes futures (ex: /settings) */}
          </Route>
        </Routes>
      </Router>
    </AuthProvider>
  );
};

export default App;
